INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (201, 'PT_PT', 'Electrostática. Estrutura atómica. Cargas e forças eléctricas. Condutores e isoladores.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (202, 'PT_PT', 'Electricidade. Potencial electrostático. Fontes de força electromotriz. Condutores, semicondutores e diódos. Corrente eléctrica. Potência eléctrica. Lei de Ohm. Resistência. Supercondutividade. Associações de resistências');
