INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (204, 'PT_PT', 'Circuitos de corrente contínua. Diagramas de circuitos. Leis dos circuitos. Método das malhas. Estado estacionário de circuitos com condensadores.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (205, 'PT_PT', 'Campo e potencial eléctricos. Campo e potencial produzidos por sistemas de sistemas de cargas pontuais. Linhas de campo e superfícies equipotenciais. Pontos críticos do campo eléctrico. Fluxo eléctrico. Lei de Gauss. Campo e potencial nos condutores');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (206, 'PT_PT', 'Campo magnético. Forças magnéticas. Torque e momento magnético. Lei de Ampère. Espiras e bobinas.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (207, 'PT_PT', 'Indução electromagnética. Campo eléctrico induzido. Leis de Faraday e de Lenz. Geradores de corrente alternada. Indutância. Autoindução.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (208, 'PT_PT', 'Processamento de sinais. Estado transitório dos circuitos. Equações diferenciais dos circuitos. Função de transferência. Constantes de tempo. Impedância generalizada. Associações de impedâncias.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (209, 'PT_PT', 'Circuitos de corrente alternada. Funções sinusoidais. Fasores. Tensão alternada. Impedância complexa. Potência dissipada nos circuitos. Filtros de frequência. Função de resposta. Ressonância.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (210, 'PT_PT', 'Ondas electromagnéticas e luz. Equações de Maxwell. Campos induzidos. Campo electromagnético no vácuo. Equação de onda. Ondas planas polarizadas. Ondas harmónicas. Espectro electromagnético. Teorias ondulatória e corpuscular da luz. Díodos emissores de luz (LED).');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (201, 'EN_UK', 'Electrostatics. Atomic structure. Electric charges and forces. Conductors and insulators.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (202, 'EN_UK', 'Electricity. Electrostatic potential. Sources of electromotive force. Conductors, semiconductors and diodes. Electric current. Electric power. Ohm''s law. Resistance. Superconductivity. Resistor associations');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (203, 'EN_UK', 'Electric capacitance. Isolated conductors. Capacitors. Electrostatic energy. Capacitor associations.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (204, 'EN_UK', 'DC circuits. Circuit diagrams. Circuit laws. Mesh method. Steady state of circuits with capacitors.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (205, 'EN_UK', 'Electric field and potential. Field and potential produced by systems of point charges. Field lines and equipotential surfaces. Critical points of the electric field. Electric flux. Gauss''s law. Field and potential in conductors');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (206, 'EN_UK', 'Magnetic field. Magnetic forces. Torque and magnetic moment. Ampère''s law. Coils and solenoids.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (207, 'EN_UK', 'Electromagnetic induction. Induced electric field. Faraday''s and Lenz''s laws. AC generators. Inductance. Self-inductance.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (208, 'EN_UK', 'Signal processing. Transient state of circuits. Circuit differential equations. Transfer function. Time constants. Generalized impedance. Impedance associations.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (209, 'EN_UK', 'AC circuits. Sinusoidal functions. Phasors. Alternating voltage. Complex impedance. Power dissipated in circuits. Frequency filters. Response function. Resonance.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (210, 'EN_UK', 'Electromagnetic waves and light. Maxwell''s equations. Induced fields. Electromagnetic field in a vacuum. Wave equation. Plane polarized waves. Harmonic waves. Electromagnetic spectrum. Wave and corpuscular theories of light. Light-emitting diodes (LEDs).');
