INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (102, 'PT_PT', 'Interacção Humano-Computador' , 'Através desta unidade curricular os estudantes ficarão a conhecer os conceitos fundamentais para compreender a interação entre humanos e sistemas computacionais. O estudante deverá:\n - compreender o papel dos factores humanos na interação e interface;\n - compreender e aplicar técnicas de prototipagem para investigação e ensaio de proposta de design;\n - desenvolver a capacidade para conceber propostas de design de interação e interface para contextos de uso específicos tendo em vista a optimização da experiência de interação, com base em abordagens contextuais e participativas;\n - desenvolver a capacidade de avaliação da usabilidade com domínio de técnicas variadas e sua adequação ao contexto de projecto;\n - desenvolver a capacidade de construção de interfaces inovadoras e coerentes com o propósito;\n - desenvolver a capacidade de combinar as técnicas de uma forma sistemática numa abordagem empírica ao design de interação;\n - desenvolver a capacidade de aprendizagem autónoma no domínio de HCI.');
