INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (116, 'PT_PT', 'Arquitectura de Software' , 'Após a conclusão desta unidade curricular, os alunos deverão:\n - ser capazes de descrever uma arquitectura com precisão;\n - reconhecer os principais estilos arquitecturais existentes em sistemas de software;\n - propor alternativas de arquitectura para um problema, e escolher entre eles;\n - ter as competências necessárias para a construção de um sistema de software de dimensão média que satisfaça uma especificação de arquitectura;\n - usar as ferramentas de desenvolvimento correctas para acelerar essas tarefas;\n - compreender a definição formal de uma arquitectura e ser capaz de raciocinar sobre as suas propriedades;\n - usar o conhecimento de domínio para especializar uma arquitectura para uma determinada família de aplicações.');
