INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (149, 'PT_PT', 'Electromagnetismo e Óptica' , 'Esta disciplina apresenta os conceitos e princípios básicos do electromagnetismo e da óptica física reforçando a compreensão desses conceitos através de aplicações ao mundo real. Os alunos deverão ter a capacidade de manipular esses conceitos e saber aplica-los à resolução de problemas. Os estudantes serão motivados por exemplos de aplicação dos princípios da física noutras áreas do conhecimento científico e tecnológico.');
