INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1797, 'PT_PT', 'Conceitos básicos: Experiência Aleatória. Acontecimentos. Conceitos frequencista e subjectivista de probabilidade. Axiomática de Kolmogorov . Probabilidade condicionada. Independência Teorema de Bayes.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1798, 'PT_PT', 'Variáveis aleatórias: Função de distribuição. Variáveis aleatórias discretas e contínuas. Valor esperado, variância e outros parâmetros. Distribuições discretas e contínuas usuais.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1799, 'PT_PT', 'Distribuições conjuntas e complementos: Distribuições conjunta, marginais e condicionadas. Independência. Correlação. Aproximações entre distribuições. Teorema do limite central. Lei dos Grandes Números.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1800, 'PT_PT', 'Amostragem e estimação pontual: Estatística descritiva versus indutiva. Amostra aleatória. Estatísticas. Estimação pontual. Propriedades dos estimadores. Método da máxima verosimilhança. Distribuições amostrais da média e variância.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1801, 'PT_PT', 'Estimação por Intervalos: Noções básicas. Intervalos de confiança para parâmetros de populações normais e outras.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1802, 'PT_PT', 'Testes de Hipóteses: Noções básicas. Testes de hipóteses para parâmetros de populações normais e outras. Testes de ajustamento de Pearson e independência em tabelas de contingência.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1803, 'PT_PT', 'Regressão Linear Simples: Estimação pelo método dos mínimos quadrados e respectivas propriedades. Inferência no modelo de regressão linear simples. Coeficiente de determinação e análise empírica de resíduos.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (1797, 'EN_UK', 'Basic concepts: Random experiment. Events. Frequentist and subjectivist concepts of probability. Kolmogorov''s axiomatics. Conditional probability. Independence. Bayes'' theorem.');
