INSERT INTO TopicTranslations(topic, language, name, description) VALUES (1 ,'PT_PT', 'Fundamentos de Requisitos de Software', 'Sobre a defininição do que é um requisito;\n\na diferença entre um requisito do produto e um requisito do processo;\n\na diferença entre requisitos funcionais e não-funcionais;\n\nrequisitos inter-dependentes;\n\ncomo classificar quantitativamente requisitos;\n\ndiferença entre requisitos de sistema e de software.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (2 ,'PT_PT', 'Processo de Requisitos', 'Sobre a natureza contínua do processo de requisitos e a sua natureza moldável ao contexto do projecto ou organização;\n\nos diferentes actores do processos (frequentemente conhecidos como “stakeholders”);\n\ncomo gerir um processo de requisitos e como avaliar a sua qualidade e ainda como o melhorar.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (3 ,'PT_PT', 'Levantamento de Requisitos', 'Sobre a origem dos requisitos (como Conhecimento próprio do Domínio, stakeholders, regras do negócio, etc.) e as técnicas para os coleccionar eficazmente (tais como entrevistas, cenários, protótipos, reuniões, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (4 ,'PT_PT', 'Análise de Requisitos', 'Sobre como classificar requisitos quanto ao seu tipo, importância, estabilidade ou extensão;\n\nmodelação conceptual (tal como “Use Cases”, modelos de dados, modelos de estados, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (5 ,'PT_PT', 'Especificação de Requisitos', 'Sobre a produção de um conjunto de documentos sobre os requisitos que possa ser sistematicamente revisto, avaliado e aprovado. Estes documentos são: a Definição do Sistema, Especificação dos Requisitos do Sistema e Especificação dos Requisitos do Software (derivados dos requisitos do sistema).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (6 ,'PT_PT', 'Validação de Requisitos', 'Sobre as técnicas de validação dos requisitos levantados, tais como: Revisão por terceiros (“peer-review”), prototipagem, validação de modelos e testes de aceitação.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (7 ,'PT_PT', 'Considerações Práticas', 'Sobre as particularidades práticas dos requisitos;\n\na natureza iterativa do processo de requisitos;\n\na gestão de alterações dos requisitos (como um todo ou em apenas alguns dos seus atributos);\n\nrastreamento de requisitos;\n\nmedição do peso dos requisitos na eventualidade de alteração do produto.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (8 ,'PT_PT', 'Fundamentos de Desenho de Software',  'Sobre os conceitos gerais de desenho (como objectivo, restrições, alternativas, contexto etc.);\n\no processo de desenho (diferença entre desenho arquitectural e detalhado);\n\nos principios do desenho de software (como abstracção, acoplamento, coesão, decomposição, modularização, encapsulamento, separação de interface e implementação, suficiência, completividade, etc.).'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (9 ,'PT_PT', 'Problemas-chave no Desenho de Software', 'Sobre os problemas típicos no desenho de software como concorrência: controlo de eventos;\n\npersistência de dados;\n\ndistribuição de componentes e comunicação entre si;\n\ntratamento de erros e tolerância a falhas;\n\ninteracção e apresentação;\n\nsegurança.'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (10,'PT_PT', 'Estrutura e Arquitectura de Software', 'Sobre os modos de organizar o software a vários níveis de abstracção desde a macro-arquitectura até ao desenho detalhado do código;\n\nos diferentes estilos de arquitectura tais como construção por camadas, sistemas distribuídos (Cliente-Servidor, Three-Tiers, Broker), sistemas interactivos (Model-View-Controller, Presentation-Abstraction-Control), sistemas adaptáveis (microkernel ou reflexão) e outros;\n\nDesign Patterns (como “builder”, “factory”, “adapter”; “bridge”, “iterator”, “strategy”, etc.);\n\no cariz decisório do processo de design;\n\nfamilías de programas e “frameworks” de software.'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (11,'PT_PT', 'Desenho de Interface com o Utilizador', 'Sobre a eficaz comunicação com o utilizador do software;\n\nprincípios base de UI (facilidade de aprendizagem, familiaridade, consistência, surpresa mínima, diversidade de utilização, etc.);\n\nlidar com problemas comuns (interacção com utilizador e  apresentação de informação);\n\nas modalidades típicas de interacção (pergunta-resposta, manipulação directa, selecção em menus, formulários, linguagens de comandos, linguagem natural);\n\ncomo apresentar a informação (número de cores, mudanças de cores, “colour-coding”, visualização para pessoas com necessidades especiais);\n\no processo de design de UI (análise de utilização, prototipagem de software, avaliação da interface com o utilizador desenvolvida);\n\nadaptar a UI para localização e internacionalização;\n\nmetáforas e modelos conceptuais (tal como utilização de ícones reconhecidos universalmente para diversas acções comuns, tal como usar uma roldana para representar as definições).'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (12,'PT_PT', 'Análise de Qualidade e Avaliação', 'Sobre os atributos de qualidade do desenho (tais como portabilidade, testabilidade, robustez, performance, segurança, reutilizável, etc.);\n\ntécnicas de avaliação de qualidade (como revista de design por terceiros, análise estática formal – com modelos matemáticos -  ou informal, simulação ou prototipagem);\n\nmedidas de software para o seu tamanho, estrutura ou qualidade.'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (13,'PT_PT', 'Notações de Desenho de Software', 'Sobre a representação do desenho em diferentes artefactos universalmente compreendidos;\n\ndescrições de estrutura (diagramas de classe e objectos, diagramas de componentes, “class responsability cards”, diagramas de Entidade-Relacionamento, linguagens de descrição de interfaces, etc.);\n\ndescrições de comportamentos e dinâmicas entre componentes (diagramas de actividade, diagramas de comunicação, diagramas de fluxo de dados, tabelas e diagramas de decisão, diagramas de sequência, diagramas de transição de estados, pseudo-código, etc.).'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (14,'PT_PT', 'Estratégias e Métodos de Desenho de Software', 'Sobre como desenhar um sistema num ou mais paradigmas bem definidos, como Orientado a Funções, Orientado a Objectos, Centrado em Estruturas de Dados e Centrado em Componentes;\n\nas estratégias gerais de desenho como “divide-and-conquer”, “top-down”, “bottom-up”, etc.'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (15,'PT_PT', 'Ferramentas de Desenho de Software','Sobre ferramentas de ajuda à criação de artefactos do desenho de software, tipicamente também software, que consiga, por exemplo, criar modelos e diagramas ou possibilite uma análise de qualidade de modelos.'); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (16,'PT_PT', 'Fundamentos de Construção de Software', 'Sobre os conceitos fundamentais da construção de software, que são: Minimização de Complexidade, Antecipação de Mudanças, Construção para Verificação, Reutilização e Standards de Construção (relativo às convenções das linguagens de programação usadas, tal como nomenclatura de variáveis ou identação, etc.).' ); 
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (17,'PT_PT', 'Gestão de Construção', 'Sobre como gerir um processo de construção através de Modelos de Ciclo de Vida (como as metodologias lineares estilo Waterfall ou iterativas tipo Agile);\n\no planeamento da construção (qual a ordem de actividades num processo de construção – isto é altamente dependente de que Modelo de construção é usado);\n\ncomo medir o que é criado (estatísticas sobre código, calendarização, esforço – novamente diferentes medidas dependem do Modelo escolhido, e.g. esforço é uma medida típica da metodologia scrum).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (18,'PT_PT', 'Considerações Práticas', 'Sobre o cariz predominantemente prático da construção;\n\na necessidade de fazer ajustamentos ou modificações de pequena escala ao Desenho durante a construção;\n\nas linguagens necessárias para construir software - como linguagens de configuração, linguagens de scripting, linguagens de programação, linguagens visuais, etc.;\n\nas características da codificação em si (construções semânticas, estruturas de controlo, tratamento de erros, organização do código em classes, modos de documentação do código, etc.);\n\ntestes de construção (testes unitários e testes de integração);\n\ntécnicas de construção para reutilização por terceiros (tais como estruturação de bibliotecas, utilização de padrões de desenho conhecidos);\n\nreutilização de software pré-existente (recurso a bibliotecas, repositórios públicos, etc.);\n\nqualidade na construção (testes de código, debugging, assertions, programação defensiva, code reviews, etc.);\n\nintegração do código num sistema (phase integration e incremental).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (19,'PT_PT', 'Tecnologias de Construção', 'Sobre como desenhar e usar APIs;\n\nproblemas típicos de paradigmas orientados aos objectos (como polimorfismo e reflexão);\n\ntipos parametrizados e genéricos;\n\nassertions, programação por interfaces e programação defensiva;\n\ntratamento de erros;\n\nmodelos executáveis (criação de um programa compilando um modelo executável de uma linguagem de modelação);\n\nprogramação baseada em estados e baseada em tabelas;\n\nconfiguração em runtime e internacionalização;\n\nprocessamento de gramáticas de linguagens;\n\nprimitivas de concorrência (semáforos, mutexes, monitores, etc.);\n\nmiddleware (software que providencia serviços entre a camada do SO e a camada aplicacional);\n\nprogramação para sistemas distribuídos;\n\nconstrução de sistemas heterogéneos (como Processadores de Sinais Digitais ou Sistemas Embebidos);\n\nanálise de performance e tuning (escolhas de arquitecturas, estruturas de dados ou selecção de algoritmos);\n\nstandards de plataformas (por exemplo J2EE vs J2SE);\n\ntest-driven development.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (20,'PT_PT', 'Ferramentas de Construção de Software', 'Sobre todo o tipo de ferramentas (tipicamente também software) que ajudam à construção de software tais como IDEs (ambientes de desenvolvimento integrado como Eclipse, Netbeans, Visual Studio, etc);\n\nconstrutores de Interfaces Gráficas (actualmente também parte de IDEs modernos);\n\nferramentas de Testes Unitários e ainda ferramentas de profiling de código, análise de performance e slicing.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (21,'PT_PT', 'Fundamentos do Teste de Software','Sobre os conceitos fundamentais de teste;\n\ndistinção entre falta, falha, erro e defeito;\n\nescolha de casos de teste;\n\neficácia e objectivos do teste;\n\nteste para descoberta de defeitos;\n\nlimites teóricos e práticos dos testes;\n\ncenários de teste impraticáveis e o relacionamento do teste com as outras actividades do ciclo de vida do software.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (22,'PT_PT', 'Níveis de Teste', 'Sobre os diferentes níveis a que um software pode ser testado;\n\nquanto ao alvo do teste (testes unitários vs testes de integração vs testes do sistema);\n\nquanto ao objectivo do teste (testes de aceitação,  testes de instalação, testes alfa e beta;\n\ntestes de confiabilidade, testes de regressão, testes de performance, testes de segurança, testes de stress, testes back-to-back, testes de recuperação, testes de interface, testes de configuração e testes de usabilidade).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (23,'PT_PT', 'Técnicas de Teste' , 'Sobre as diferentes técnicas usadas para encontrar o maior número de falhas possíveis;\n\ntécnicas baseadas na Intuição do Engenheiro (ad hoc e testes exploratórios);\n\ntécnicas baseadas no domínio do input (particionamento equivalente, teste combinatório, teste de valores-limite, testes aleatórios);\n\ntécnicas baseadas em código (testes baseados nos fluxos de controlo do código, testes do fluxo de dados);\n\ntécnicas baseadas nas falhas (error guessing, testes de mutação);\n\ntécnicas baseadas no uso (teste ao típico perfil operacional, heurísticas de observação do utilizador);\n\ntécnicas baseadas em modelos (tabelas de decisão, máquinas de estado;\n\nespecificações formais, modelos de workflow);\n\ntécnicas baseadas na natureza da aplicação (aplicação web vs aplicação mobile, etc.);\n\ncombinação das técnicas descritas.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (24,'PT_PT', 'Métricas Relacionadas com o Teste', 'Sobre as diferentes métricas usadas para avaliar a qualidade dos testes;\n\nmétricas de avaliação do programa (métricas de tamanho, falhas típicas para esse tipo de software, densidade das falhas, avaliação da confiabilidade, modelos de crescimento da confiabilidade);\n\nmétricas de avaliação dos testes realizados (cobertura total de testes, introdução de falhas, resultados das mutações).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (25,'PT_PT', 'Processo de Teste' , 'Sobre a forma de organizar as actividades, estratégias e técnicas de teste num processo controlado;\n\nconsiderações práticas (criação de guias de teste, organizadores do processo, criação de documentação de testes, test-driven development, criação de uma equipa de testes independente ou interna, estivativas de esforço e teste, definição de terminação de um testes, reutilização de testes e utilização de um padrão de testes);\n\nactividades de teste ( planeamento de teste, geração de casos de teste, desenvolvimento de ambientes de treino, execução sistemática de testes, avaliação do resultado dos mesmos, registo dos resultados ao longo do tempo, rastreamento de defeitos).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (26,'PT_PT', 'Ferramentas de Teste de Software' , 'Sobre as ferramentas, tipicamente também software, que servem de auxilio à escolha realização e avaliação de testes.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (27,'PT_PT', 'Fundamentos da Manutenção de Software','Sobre os conceitos básicos, terminologia e definições de manutenção de software;\n\no papel específico de um engenheiro responsável por manutenção de software;\n\nas necessidades de manutenção (corrigir falhas, melhorar o desenho do software, adaptar a novos ambientes, etc.);\n\ncustos de manutenção do software;\n\nevolução do software como uma das finalidades de manutenção;\n\nas diferentes categorias de manutenção (correctiva, adaptativa, perfectiva e preventiva).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (28,'PT_PT', 'Problemas-chave da Manutenção de Software', 'Sobre os principais problemas que um responsável pela manutenção de software enfrenta tais como questões técnicas (compreensão limitada do software em questão, testes a um sistema em manutenção, análise de impacto da manutenção proposta, grau de modificabilidade de um sistema em produção);\n\nquestões relacionadas com a gestão do processo de manutenção (alinhar o processo de manutenção de um software com os objectivos da organização responsável, gerir e motivar responsáveis pela manutenção, outsourcing de manutenção);\n\nestimativas de custo de manutenção;\n\nmétricas de manutenção.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (29,'PT_PT', 'Processo de Manutenção', 'Sobre um ciclo de vida de uma fase de manutenção (analisar problemas, implementar as modificações, aprovar, descontinuar o produto, migrar, etc.);\n\nas actividades exclusivas de um engenheiro responsável pela manutenção (análise do estado do software, tarefas de suporte, planeamento da manutenção em termos de prazos e âmbito da manutenção, gestão da configuração do software em manutenção, garantir a qualidade do software em manutenção).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (30,'PT_PT', 'Técnicas de Manutenção', 'Sobre as técnicas utilizadas para manter e/ou actualizar um software com sucesso;\n\ncompreensão do código do software;\n\ntécnicas de alteração sistemática do software (como refactoring);\n\ntécnicas de engenharia reversa de um software;\n\ntécnicas de migração de software;\n\ntécnicas e políticas de descontinuação de software.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (31,'PT_PT', 'Ferramentas de Manutenção de Software', 'Sobre as ferramentas que auxiliam à manutenção de software, tais como ferramentas de corte (slicing) de software (que isolam as partes a modificar), analisadores estáticos e dinâmicos de código;\n\nanalisadores de fluxos de dados e de dependências e ainda ferramentas de auxílio à engenharia reversa.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (32,'PT_PT', 'Gestão do processo de Gestão de Configurações', 'Sobre como organizar uma gestão de configurações;\n\nquais as restrições impostas ao processo;\n\nsobre o planeamento típico de um processo de gestão de configurações (quem é o responsável, qual a calendarização do projecto, quais as ferramentas a ser usadas, que configurações providenciar a terceiros, as interacções potenciais entre o software a ser controlado e externos);\n\ndocumentação das configurações e sua gestão e utilização do mesmo como guidelines numa organização;\n\ngarantia da aplicação correcta da configurações da organização seja através da aplicação de métricas, ferramentas próprias ou simples auditorias.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (33,'PT_PT', 'Identificação de Configurações de Software', 'obre a identificação das entidades a serem sujeitas a controlo de configurações, quais os relacionamentos entre si, como definir o seu versionamento, regras de criação de uma baseline, políticas de integração de um item de software (um ficheiro, código-fonte, recursos visuais, etc) no controlo de configurações;\n\nutilização e gestão de software libraries.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (34,'PT_PT', 'Controlo de Configurações de Software', 'Sobre o controlo do fluxo normal de um processo de gestão de alterações na configuração do software, como requisição de alteração (vulgo change request), avaliação e aceitação/rejeição da alteração proposta;\n\nimplementação da alteração e seu rastreamento e por fim autorizações de desvio (da funcionalidade a alterar) ou renúncia (idem).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (35,'PT_PT', 'Registo de Estado da Configuração de Software', 'Sobre o registo e relato da evolução do estado das configurações de software ao longo do tempo;\n\ncomo capturar o estado de itens de software a cada momento e como reportá-los à organização.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (36,'PT_PT', 'Auditoria de Configurações de Software', 'Sobre o processo de escrutinar as configurações de software de modo a garantir que o seu funcionamento está alinhado com os requisitos especificado (como um todo ou a nível de determinada baseline), a arquitectura/desenho planeada e se está alinhado com a documentação.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (37,'PT_PT', 'Gestão de Lançamento e Entrega de Software', 'Sobre como organizar a entrega de uma ou mais versões de software;\n\ncomo executar um processo de build de software (empacotamento, distribuição de diferentes versões em simultâneo, diferentes ambientes de produção;\n\na entrega de um produto de software completo (quanto efectuar a entrega com base no estado do software, quais os restantes artefactos entregáveis – como documentação e notas de entrega).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (38,'PT_PT', 'Ferramentas de Gestão de Configurações de Software', 'Sobre os tipos de ferramentas utilizados para suportarem a eficaz gestão de configurações, tais como ferramentas de controlo de versões, ferramentas de build, ferramentas de suporte a problemas de clientes, ferramentas de controlo de workspaces para equipas de desenvolvimento, ferramentas de issue-tracking, etc.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (39,'PT_PT', 'Inicialização e Definição de Âmbito', 'Sobre como determinar e negociar requisitos de software;\n\nanálise de viabilidade (avaliação da capacidade de realizar os requisitos com base em todas as restrições a que está sujeito - como custos, tecnologias, recursos humanos, políticas, etc.);\n\nprocessos de revisão de requisitos (quais as regras acordadas com stakeholders para fazer eventuais alterações aos requisitos).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (40,'PT_PT', 'Planeamento do Projecto de Software', 'Sobre como planear um projecto de software;\n\nqual o modelo de ciclo de vida de software a usar (tal como cascata, espiral ou métodos ágeis) e que ferramentas a usar para garantir a sua aplicação;\n\nescolha dos artefactos entregáveis (documentação, software instalável, etc.);\n\nestimação de custos, esforço e temporização;\n\nalocação de recursos humanos a tarefas;\n\ngestão de risco (identificação, análise de impacto, prioritização de factores de risco, desenvolvimento de estratégias de atenuação de risco, etc.);\n\ngestão da qualidade (definição de métricas de qualidade, garantir a aplicação de controlo de qualidade durante o projecto).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (41,'PT_PT', 'Execução do Projecto de Software', 'Sobre a aplicação do planeamento ao desenvolvimento do projecto;\n\naplicação dos termos contratados com o receptor do software (o cliente);\n\naplicação das medições e métricas planeadas;\n\nmonitorizar e adaptar os planos consoante a evolução do projecto e em alguns casos abandonar o mesmo;\n\norganização e desenvolvimento de relatórios sobre o progresso.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (42,'PT_PT', 'Revisão e Avaliação', 'Sobre o acompanhamento e avaliação periódica do projecto quanto aos objectivos e satisfação do cliente;\n\ndeterminar a satisfação do cliente quanto aos requisitos;\n\navaliação e revisão da performance dos métodos, ferramentas e recursos aplicados no projecto.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (43,'PT_PT', 'Término do Projecto', 'Sobre a finalização do projecto na sua totalidade ou numa milestone;\n\ndeterminar a âmbito de “término” quer a nível total ou parcial (e.g. um número pré-acordado de requisitos implementados pode ser um término de fase assim como todos os requisitos acordados e a respectiva documentação pode ser o término total);\n\nas actividades de término de projecto (como o arquivamento de documentação, por quanto tempo manter documentação, actualização de estados de projecto, elaborar documentos de “Lições aprendidas”, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (44,'PT_PT', 'Medições de Engenharia de Software', 'Sobre a aplicação de medições ao nível do projecto, processos e produtos de trabalho;\n\nestabelecimento e incentivo de medições (quais as medições, qual o âmbito, aplicação das medições pelas equipas, etc.);\n\nplaneamento de processos de medições;\n\naplicação de medições nos processos com todos os dados relevantes coleccionados e documentação das mesmas;\n\navaliação das medições e aplicação de correcções ou melhoramentos se necessário.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (45,'PT_PT', 'Ferramentas de Gestão de Engenharia de Software', 'Sobre as ferramentas usadas para auxílio à gestão de um projecto de software (planeamento e acompanhamento do estado de projecto, ferramentas de gestão de risco, ferramentas de comunicação e ferramentas de medição). Estas são também tipicamente software.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (46,'PT_PT', 'Definição de Processo de Software', 'Sobre a definição daquilo que é um processo de software e o que o compõe;\n\ninputs requeridos, actividades de trabalho transformativas e os outputs gerados;\n\ndecomposição do processo em unidades de trabalho;\n\ncargos e competências;\n\ntécnicas, ferramentas e notações;\n\ngestão de processos de software, a sua eficiência para um projecto e os impactos que advenham da sua troca por outro.'  );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (47,'PT_PT', 'Ciclos de Vida de Software', 'Sobre as diferentes categorias de modelos de ciclos de vida do desenvolvimento de software;\n\ncategorias de processos de software, tais com processos primários (para desenvolvimento, operação e manutenção de software), processos de suporte (para gestão das configurações, controlo de qualidade, verificação e validação), processos organizacionais (treino, gestão de infra-estruturas, gestão de portefolio, etc.), processos inter-projectos (transversais a vários projectos);\n\nmodelos de ciclo de vida do software (modelos preditivos ou lineares como o modelo cascata vs modelos iterativos como o modelo espirar vs modelos ágeis de desenvolvimento como scrum);\n\nadaptação de processos de software existentes às necessidades do projecto, organização ou desenvolvimentos tecnológicos.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (48,'PT_PT', 'Avaliação e Melhoramento de Processos de Software', 'Sobre o acompanhamento do desempenho dos processos e a sua potencial alteração de modo a melhor responderem às necessidades específicas do projecto ou organização;\n\nos diferentes modelos e métodos (qualitativos que são sujeitos ao julgamento especializado ou quantitativos que são realizados com base em métricas);\n\nmodelos de melhoria iterativa de processos (como por exemplo o Plan-Do-Check-Act);\n\nsistemas de avaliação e pontuação de processos.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (49,'PT_PT', 'Medidas para Processos de Software', 'Sobre as diferentes medidas e técnicas de medição usadas para os processos de software;\n\nmedidas de determinação da eficiência de um processo (como medidas de esforço, horas-homem, dias-homem, custos monetários do processo ou fase de processo) ou do produto em si;\n\na qualidade das medidas a usar para processos e produtos;\n\nrecurso a software com modelos de análise de processos que permitem testar vários parâmetros dos processos usados e determinar possíveis pontos de melhoria;\n\ntécnicas de medição de processos quantitativas (medição directa de número de defeitos, horas-homem, entre outras e respectiva análise por métodos estatísticos) e técnicas qualitativas (como questionários ou entrevistas).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (50,'PT_PT', 'Ferramentas de Processos de Engenharia de Software', 'Sobre todo o tipo de ferramentas relacionadas com os processos de software, com por exemplo diagramas de BPMN, folhas de cálculo, diagramas de UML ou até software de gestão de projectos.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (51,'PT_PT', 'Modelação', 'Sobre os princípios gerais de modelação de software, sendo estes “Modelar o essencial”, “Providenciar uma perspectiva sobre o problema” e “Permitir a comunicação eficaz”;\n\nas propriedades de modelos (completude, consistência e exactidão) e expressão de modelos (conceito de entidade e relacionamento e os seus significados de acordo como tipo de modelo);\n\na sintaxe e a semântica das linguagens de modelação (textuais ou gráficas);\n\nos conceitos de pré-condição, pós-condição e invariantes.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (52,'PT_PT', 'Tipos de Modelos', 'Sobre os diferentes tipos de modelos usados para software;\n\nmodelos de informação (que identificam e definem um conjunto de conceitos, propriedades, relacionamentos e restrições em entidades de dados);\n\nmodelos comportamentais (identificam e definem as funções do software a ser modelado, os exemplos comuns são máquinas de estado, modelos de controlo de fluxo e modelos de fluxos de dados);\n\nmodelos estruturais (definem a composição lógica ou física das suas diferentes partes, exemplos disso são os diagramas de classe, de objectos e de packages).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (53,'PT_PT', 'Análise de Modelos', 'Sobre as técnicas de inspecção crítica de modelos pelo engenheiro;\n\nanálise de completude;\n\nanálise de consistência;\n\nanálise de exactidão;\n\nrastreamento entre requisitos, modelos e funcionalidade implementada;\n\nanálise de interacção entre modelos e funcionalidade.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (54,'PT_PT', 'Métodos de Engenharia de Software', 'Sobre as abordagens organizadas e sistemáticas ao desenvolvimento de software para um computador alvo;\n\nMétodos Heurísticos, que são baseados na experiência e largamente praticados pela indústria (tais como métodos de Análise Estruturada que tratam o software como um conjunto hierárquico de funções, métodos de Modelação de Dados que se focam na forma e relacionamento dos dados para organizações cujo princial activo é precisamente os dados e por fim Métodos Orientados aos Objectos, cujo software é um conjunto de objectos que representam o mundo real, encapsulam dados e têm comportamento);\n\nMétodos Formais que utilizam uma notação matemática rigorosa que permite definir, refinar, derivar e verficar software;\n\nMétodos de Prototipagem cuja finalidade é desenvolver uma pequena parte ou parte incompleta do software (normalmente a menos bem compreendida) de modo a poder refiná-la com recurso a input crítico do protótipo;\n\nMétodos Ágeis de desenvolvimento, caracterizados por desenvolvimentos iterativos curtos com alta participação de elementos da equipa (métodos como Extreme Programming, scrum ou Cristal Clear).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (55,'PT_PT', 'Fundamentos da Qualidade de Software', 'Sobre os princípios gerais do que define qualidade para os stakeholders;\n\na instalação de uma cultura e uma ética de trabalho orientada à qualidade;\n\no valor de desenvolver software de qualidade e o impacto que isso tem  nos custos do mesmo;\n\ncaracterísticas e modelos de qualidade a nível de processos e do produto em si;\n\ntécnicas de melhoramento de qualidade (como Plan-Do-Check-Act, kaizen ou Quality Function Deployment);\n\nconceitos de software para sistemas críticos e o impacto que a qualidade tem nestes sistemas.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (56,'PT_PT', 'Processos de Gestão de Qualidade de Software', 'Sobre os processos que asseguram que produtos de software, serviços e modelos de ciclo de vida respondem aos objectivos  organizacionais de qualidade e satisfação de stakeholders;\n\na garantia de qualidade de software (vulgo Quality Assurance);\n\nverificação e validação da qualidade;\n\nrevisão e auditoria de processos e produtos de software.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (57,'PT_PT', 'Considerações Práticas', 'Sobre os problemas práticos que têm um impacto na qualidade do software: o modo como os requisitos influenciam a gestão da qualidade, tais como o tipo de segurança do sistema, o ambiente físico, o orçamento do projecto, o grau de dependência do mesmo (software crítico implica um processo de qualidade elevado);\n\nos diferentes tipos de defeitos do software (erro computacional, erro humano, defeito, falta e falha);\n\nas técnicas de gestão da qualidade do software, como técnicas estáticas (análise de código-fonte, análise de design, métodos formais, etc.), técnicas dinâmicas (execução de código, simulação, debugging), teste de software executado por individuos fora da equipa de desenvolvimento para verificar a conformidade do software à sua especificação;\n\nmedição da qualidade do software (com recurso a métricas de qualidade, análise estatística, análise de tendência, etc.) com a finalidade de ter uma vista geral sobre a qualidade do produto e sobre as partes problemáticas do mesmo.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (58,'PT_PT', 'Ferramentas de Qualidade de Software', 'Sobre as ferramentas de auxílio aos processos de qualidade de software, tais como ferramentas de análise estática de sintaxe e semântica de código (tal como compiladores, Java Mission Control, etc) e ferramentas de análise dinâmica de código (como debuggers).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (59,'PT_PT', 'Profissionalismo', 'Sobre a aderência do engenheiro a códigos de ética, conduta profissional e práticas standard estabelecidas pela sua comunidade profissional;\n\nacreditação, certificação e licenciamento;\n\ncódigos de ética e conduta profissional definidos pela sua comunidade profissional (como o código de ética da ACM e IEEE);\n\nas sociedades profissionais e a sua função (por exemplo a IEEE ou a Ordem dos Engenheiros);\n\nos Standards de Engenharia de Software (por exemplo os múltiplos standards da IEEE para Engenharia de Software);\n\no impacto económico do software;\n\nos variados tipos de contratos de trabalho e características;\n\nas questões legais (como marcas registadas, propriedade intelectual, uso justo, cibercrime, direitos de cópia);\n\na criação de documentação e os seus trâmites;\n\nnegociações (de requisitos, funcionalidades, contratos, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (60,'PT_PT', 'Dinâmica de Grupo e Psicologia', 'Sobre as questões relacionadas com trabalho em equipa e diferentes grupos de individuos como clientes ou colaboradores;\n\nquestões relacionadas com trabalho em grupo (honestidades intelectual, admissão de ignorância, admissão de erros, adaptabilidade);\n\nos factores que afectam a cognição pessoal (volume de dados, complexidade de problemas, incapacidade de exprimir os problemas, incapacidade de consultar com outros, etc.);\n\ncomo lidar com problemas complexos (trabalho em equipa, decomposição de problemas, etc.);\n\ncomo interagir com stakeholders;\n\nlidar com incerteza e ambiguidade nos problemas de desenvolvimento;\n\nlidar com problemas de multiculturalismo de equipas ou clientes.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (61,'PT_PT', 'Habilidades de Comunicação', 'Sobre as capacidades de comunicação orais e de escrita do engenheiro;\n\nleitura de material técnico, compreensão e resumo;\n\nhabilidades de escrita de documentos relevantes aos processos de software (requisitos, documentação de software, contratos, etc.);\n\ncomunicação eficaz com equipas ou grupos;\n\nhabilidades de criar e executar apresentações (de ideias, de produtos, pitches, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (62,'PT_PT', 'Fundamentos de Economia', 'Sobre os conceitos base de Economia;\n\nfinanças e contabilidade;\n\ncontrolo de custos;\n\ncash flow;\n\nprocessos de tomada de decisões (orientada à Economia);\n\nconceito de valorização;\n\ninflação e depreciação;\n\ntaxação (impostos);\n\nvalor temporal do dinheiro (a oscilação do valor do dinheiro ao longo do tempo);\n\neficiência económica;\n\neficácia;\n\ndefinição de produtividade.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (63,'PT_PT', 'Economia de Ciclos de Vida', 'Sobre os conceitos económicos aplicados à actividade do software ao longo do seu ciclo de vida;\n\nconceitos de produto, projecto, programa, portefolio, ciclo de vida de um produto, ciclo de vida de um projecto, propostas e tipos de propostas;\n\ndecisões de investimento;\n\nhorizonte de planeamento (sobre os custos económicos relacionados com investimentos num determinado prazo, como o tempo de vida de um produto de sotware e o seu impacto económico numa empresa);\n\nconceito de preço e estabelecimento de políticas de preços;\n\nconceito de custo e gestão de custos;\n\nmedição de performance económica;\n\ngestão do valor ganho (técnica económica que permite um controlo eficaz de custos comparando o valor já criado com o orçamento projectado e com o custo calendarizado até esse momento);\n\ndecisões de terminação de um produto baseadas em economia;\n\ndecisões de troca (troca de um software antiquado por um novo em vez de o manter) ou descontinuação (de um produto ou de actividade como um todo).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (64,'PT_PT', 'Risco e Incerteza', 'Sobre os conceitos de objectivo económico, estimativas e planeamento;\n\ntécnicas de estimação (como analogias, estimação por partes, métodos estatísticos, etc.);\n\nreagir a incerteza (como utilizar estimativas de diversos âmbitos, adiar decisões finais, etc.);\n\ntécnicas de prioritização;\n\ntécnicas de tomada decisões sob risco (como árvores de decisão, análise de Monte Carlo, etc.);\n\ntécnicas de tomada de decisões sob incerteza (como Regra de Laplace, Regra de Hurwicz, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (65,'PT_PT', 'Métodos de Análise Económica', 'Sobre os diferentes métodos de análise ecónomica aplicáveis dependendo do tipo de organização, finalidade ou objectivos do software: análise de decisão de organizações com fins lucrativos;\n\nmínimo rácio de retorno aceitável (critério de escolha de investimentos baseado num retorno percentual mínimo);\n\nretorno de investimento (rácio entre o valor ganho relativamente ao investido);\n\nretorno do capital aplicado;\n\nanálise custo-benefício;\n\nanálise custo-efectividade;\n\nanálise de break-even (igualdade entre custos e rendimentos);\n\ncaso de negócio (apresentação estruturada e consolidada sobre uma proposta de negócio a partir de diferentes perspectivas de decisão como custos, benefícios, risco, etc.);\n\navaliação a multiplos atributos (a nível de outras propriedades que não simplesmente dinheiro);\n\nanálise de optimização.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (66,'PT_PT', 'Considerações Práticas', 'O princípio do “Suficientemente Bom” (quando o custo de adicionar uma nova funcionalidade - ou investir em algo não essencial - não pode ser medido este princípio é um indicador de quando o estado desta funcionalidade é suficientemente boa para ser entregue);\n\nmercados sem fricção (sempre que existem entraves a uma competição perfeita, sejam custos de transporte, estabelecimentos, proteccionismo e, particularmente no software, não-neutralidade de internet ou limitação de venda de aplicações em lojas especificas);\n\necossistemas (quando as qualidades ou atributos de um produto são melhoradas devido à existência de terceiros que de alguma forma interagem com  produto, como por exemplo, uma aplicação que é integrada ou configurada à medida por uma empresa externa);\n\noffshoring (o desenvolvimento de uma actividade económica fora do território de operação da organização por razões de custos ou de défices de qualidade) e outsourcing (a execução de tarefas para uma organização por uma entidade externa quando essas actividades poderiam ser feitas pela organização).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (67,'PT_PT', 'Técnicas de Resolução de Problemas', 'Sobre a definição de resolução de problemas;\n\nformulação de problemas reais;\n\nanálises de problemas;\n\ndesenhar uma estratégia de procura de soluções;\n\nresolução de problemas com recurso a programas de computadores.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (68,'PT_PT', 'Abstracção' ,'Sobre os conceitos inerentes à abstracção - genericamente definida como “o processo e o resultado da generalização através da redução da informação a um conceito, um problema ou um fenómeno observável de modo a permitir o foco no panorâma geral;\n\nos níveis de abstracção;\n\nconceito de encapsulamento;\n\nhierarquia de abstracções.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (69,'PT_PT', 'Fundamentos de Programação' ,'Sobre os conceitos-base de programação – a criação de programas de computador com recurso a código;\n\no processo de programação (desenho, escrita, teste, debug e manutenção);\n\nas características dos diferentes paradigmas de programação (não-estruturada, estruturada, procedimental, imperativa, orientada aos objectos, funcional, orientada aos aspectos, etc).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (70,'PT_PT', 'Bases de Linguagens de Programação' ,'Sobre as linguagens em que é possível escrever código com a finalidade de criar programas para um computador;\n\na sintaxe e semântica de uma linguagem;\n\nlinguagens de baixo nível, linguagens de alto nível;\n\ndiferenças entre linguagens declarativas e imperativas.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (71,'PT_PT', 'Técnicas e Ferramentas de Debugging' ,'Sobre o processo de encontrar e corrigir problemas no software escrito;\n\nos diferentes tipos de erros (de sintaxe, erros lógicos, erros de dados, etc.);\n\ntécnicas de debugging (um passo de cada vez, recurso a breakpoints, recurso a pontos de observação, etc.);\n\nferramentas de debugging (como debuggers ou ferramentas de análise de código estático).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (72,'PT_PT', 'Estruturas e Representação de Dados' ,'Sobre a organização e formas de expressão de dados para uso por programas;\n\ntipos de estruturas de dados (listas, filas, pilhas, tabelas, tabelas de hash, etc.);\n\nas diferentes operações em diferentes estruturas de dados (CRUD – create,read,update e delete, operações de encontro de elementos, travessia, ordenação).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (73,'PT_PT', 'Algoritmos e Complexidade' ,'Sobre a escrita organizada de programas em algoritmos – uma estratégia definida numa série de passos com vista à resolução de um problema;\n\nos atributos de algoritmos (modularidade, funcionalidade, robustez, simplicidade, etc.);\n\nanálise de algoritmos (análise do pior caso, análise do caso médio, notação Big-O,etc.);\n\nestratégias de design de algoritmos (força-bruta, divide and conquer, algoritmos gulosos, programação dinâmica, etc.);\n\nestratégias de análise de algoritmos (contagem de passos, análise assimptótica, análise probabilística, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (74,'PT_PT', 'Conceitos Básicos de um Sistema' ,'Sobre a definição de um sistema;\n\nas propriedades de um sistema;\n\npropriedades emergentes de um sistema;\n\nengenharia de sistemas e bases sobre um sistema de computadores.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (75,'PT_PT', 'Organização de um Computador' ,'Sobre os quatro níveis em que se divide a organização de um computador (as macro-arquitecturas, micro-arquitecturas, circuitos lógicos e dispositivos);\n\nsistemas digitais (baseados em sinais e código binário);\n\nlógica digital (as operações sobre números binários como AND, OR, etc.);\n\na expressão de dados binários num computador;\n\na unidade de processamento central e o seu funcionamento (CPU);\n\na organização de memória;\n\no funcionamento de dispositivos de I/O.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (76,'PT_PT', 'Bases de Compiladores','Sobre o que é um compilador;\n\nas diferenças entre compiladores e interpretadores;\n\no processo de compilação.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (77,'PT_PT', 'Bases de Sistemas Operativos' ,'Sobre os conceitos de um sistemas operativo;\n\nas tarefas de um sistemas operativo;\n\nos diferentes tipos de sistemas operativos (batch, sistemas em time-sharing, sistemas em tempo real, sistemas operativos distribuídos, sistemas embebidos, sistema operativo de servidor, sistema operativo móvel, sistema operativo de computador pessoal, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (78,'PT_PT', 'Bases de Dados e Gestão de Dados' ,'Sobre os conceitos de uma base de dados;\n\nentidade e esquema;\n\nos diferentes sistemas de gestão de bases de dados (Oracle, MS SQL Server, Postgre, etc.);\n\nlinguagem de interpelação de bases de dados – como SQL – as suas características e como a usar;\n\norganização e manipulação de um sistema de gestão de bases de dados;\n\nmineração de dados (a descoberta de padrões e relações pouco óbvias entre os dados).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (79,'PT_PT', 'Bases de Comunicação em Redes', 'Sobre os diferentes tipos de redes (rede pessoal, rede local, wide area network, internet, etc.);\n\nos conceitos básicos de redes (os seus componentes e as diferentes funções destes);\n\nos protocolos e standards de redes (pilha OSI e seus componentes como TCP/IP, UDP, Bluetooth, etc.);\n\na Internet;\n\na Internet das Coisas;\n\nredes virtuais privadas (vulgo VPN).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (80,'PT_PT', 'Computação Paralela e Distribuída' , 'Sobre os conceitos básicos sobre o que é computação paralela e computação distribuída;\n\nas diferenças entre estes dois paradigmas;\n\nmodelos de computação paralela (memória partilhada, passagem de mensagens, etc.);\n\nos principais problemas na computação distribuída.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (81,'PT_PT', 'Factores Básicos de Utilização Humana' , 'Sobre os problemas típicos que advêm da utilização de software pelos utilizadores humanos;\n\nconsiderações sobre o I/O (como mostrar o output a utilizadores, qual o formato mais adequado, qual a maneira mais agradável de apresentar, etc.);\n\nmensagens de erro (como explicar claramente o que aconteceu e como resolver, qual o tipo de linguagem, etc.);\n\nrobustez de software (como o software tolera o uso incorrecto por pessoas, como recuperar de maneira a não desincentivar o uso por pessoas, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (82,'PT_PT', 'Factores Básicos de Desenvolvimento por Humanos' , 'Sobre os problemas comuns que advêm de o software ser construído e mantido por humanos e como a sua escrita afecta estas actividades;\n\ncomo estruturar o código para facilitar compreensão;\n\ncomo escrever comentários que facilitem o trabalho dos responsáveis presentes e futuros do software.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (83,'PT_PT', 'Desenvolvimento e Manutenção Segura' , 'Sobre as práticas e regras seguidas com a finalidade de tornar o software mais seguro em todas as suas fases, quer a nível de requisitos, como construção e teste.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (84,'PT_PT','Conjuntos, Relacionamentos e Funções' ,'Sobre os conceitos matemáticos e lógicos sobre os conjuntos (colecção de elementos), as suas propriedades e as suas operações (intesecção, união, produto cartesiano, etc.);\n\nsobre relacionamentos (associações entre dois conjuntos de informação);\n\nsobre funções matemáticas (uma relação de comportamento conhecido que exprime o mapeamento entre elementos de domínios).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (85,'PT_PT','Lógica Básica' , 'Sobre a lógica proposicional, a sua terminologia e leis (variáveis Booleanas, operações booleanas, Leis de Identidade, Leis Distributivas, Leis de De Morgan, etc.): a lógica de Predicados, as suas propriedades, terminologia e leis.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (86,'PT_PT','Técnicas de Prova' ,'Sobre os argumentos que estabelecem rigorosamente a verdade de uma afirmação;\n\nos métodos de prova de um teorema (provas por contradição, provas por indução, etc.).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (87,'PT_PT','Bases da Contagem' ,'Sobre as acções que permitem encontrar o número de elementos num conjunto finito de elementos e suas regras (regra da soma, regra do produto, etc.) e princípios;\n\nprincípio da inclusão-exclusão;\n\ndefinição de recursividade, aleatoriedade, probabilidade, permutação e combinação.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (88,'PT_PT','Grafos e Árvores' , 'Sobre os diferentes tipos de grafos (grafos direccionados, grafos não-direccionados, etc.), a sua terminologia (vértices, nós, peso, arestas, etc.) e operações (travessia, conexões, etc.);\n\nos diferentes tipos de árvores, as suas propriedades e operações (travessias, adições, etc.).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (89,'PT_PT','Probabilidade Discreta' ,'Sobre a descrição matemática da aleatoriedade a sua terminologia, propriedades e operações.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (90,'PT_PT','Máquinas de Estado Finitas' , 'Sobre as máquinas de estados finitos (a abstracção matemática composta por um número finito de estados e as transições entre estes), a sua terminologia e operações.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (91,'PT_PT','Gramáticas' , 'Sobre as gramáticas de linguagens formais, a sua terminologia (vocabulário, frase, terminais, símbolo, etc.);\n\nos tipos de gramáticas existentes (gramática livre de contexto, gramática sensível ao contexto, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (92,'PT_PT','Precisão numérica, exactidão e erros' , 'Sobre os problemas que recorrem na representação de números;\n\no carácter finito da precisão de representação de números num computador;\n\ntécnicas de arredondamentos;\n\nalgarismos significativos;\n\ntipos de erros (relativos, absolutos).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (93,'PT_PT','Teoria dos Números' ,'Sobre os números;\n\nos tipos de números (inteiro, racional, real, complexo, imaginário etc.);\n\nterminologia e operações (divisão, mod, módulo);\n\nos números primos, a sua utilidade a sua aplicação (como criptografia).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (94,'PT_PT','Estruturas Algébricas' ,'Sobre as estruturas algébricas (definidas como conjuntos sujeitos a algumas operações e que satisfaçam um número de axiomas);\n\nsobre grupos, as suas propriedades (associação, identidade, fecho, etc.) e tipos (monóide, subgrupo);\n\nanéis (um conjunto com duas operações binárias - normalmente adição e multiplicação - onde cada operação combina dois elementos para formar um terceiro).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (95 ,'PT_PT','Métodos Empíricos e Técnicas Experimentais' ,'Sobre como propôr soluções ou modelos de solução e realizar experiências para resolver problemas;\n\ncriação de experiências controladas (formular hipóteses, criar pré-condições, observar variáveis, relacionamento entre resultados, etc.);\n\nestudo observacional com recurso a contextualização;\n\nestudo retrospectivo, também conhecido como estudo histórico (análise de  dados históricos com a finalidade de encontrar relacionamentos entre dados e prever eventos futuros.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (96 ,'PT_PT','Análise Estatística' ,'Sobre a capacidade de atingir resultados generalizaveis através do estudo de uma população limitada de amostras de resultados prévios (i.e. com recurso a estatística);\n\nos conceitos estatísticos de amostra, população, unidade de análise, variável aleatória, evento, distribuição de variáveis, estimação, tipos de estimativas, propriedades de estimadores e testes de hipóteses estatísticas;\n\ncorrelação e regressão.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (97 ,'PT_PT','Medição' ,'Sobre a problemática da medição no software e as suas diferenças fundamentais para outros atributos de medição mais simples;\n\nos conceitos de níveis (ou escalas) de medição (nominal, ordinal, intervalar e por rácios);\n\nmedidas directas versus medidas indirectas;\n\nconfiabilidade e validade de uma medida;\n\navaliação de confiabilidade de medidas.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (98 ,'PT_PT','Desenho de Engenharia' ,'Sobre os desafios que a elaboração de uma solução estruturada para um problema de engenharia representam para o engenheiro, sendo que o desenho neste caso não tem nada que ver com o seu conceito tradicional - a representação gráfica - mas sim com o planeamento ou arquitectura de uma solução para um problema, o seu processo e as suas fases (definição de um problema, obtenção de informação pertinente, geração de múltiplas soluções, análise e selecção de uma solução, implementação da solução).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (99 ,'PT_PT','Modelação, Simulação e Prototipagem' ,'Sobre os conceitos e técnicas de modelação (o processo de representação de parte ou totalidade de um sistema recorrendo a abstracções) e os seus diferentes tipos (icónico, analógico e simbólico);\n\no conceito de simulação (condução de experiências em modelos com a finalidade de melhorar a compreensão sobre o mesmo) e a sua construção;\n\no conceito de protótipo (uma representação inicial, parcial ou incompleta de um sistema) e a sua utilização.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (100,'PT_PT','Standards' ,'Sobre o conceito de standard (requisito, especificação, guia ou característica que deve ser observado por engenheiros de modo que produtos, processos e materiais tem níveis aceitáveis de qualidade);\n\nos diferentes tipos de standards existentes;\n\na conformidade mensurável com standards;\n\nas diferentes entidades que produzem standards internacionais de engenharia.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (101,'PT_PT','Análise de Causa' ,'Sobre o processo de investigação e identificação de como e porque aconteceu um evento indesejável e sua causa de raíz (i.e. o que deu origem ao evento);\n\nas principais técnicas para conduzir este tipo de análise (métodos de revisão, diagramas de estado presente vs estado desejável, revisão por terceiros, árvores de falha, etc.);\n\nas ferramentas de auxílio à análise (diagramas de causa-efeito, histogramas, diagramas de Pareto, etc.).' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (102,'PT_PT','Nenhuma das Áreas do Conhecimento', 'Para qualquer tópico do programa que não encaixa em qualquer das Áreas do Conhecimento.' );
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (1, 'EN_UK', 'Software Requirements Fundamentals', 'Covers the definition of a software requirement; the difference between a product requirement and a process requirement; the difference between functional and non-functional requirements; interdependent requirements; how to classify requirements quantitatively; and the difference between system and software requirements.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (2, 'EN_UK', 'Requirements Process', 'Covers the continuous nature of the requirements process and its adaptability to the context of the project or organization; the different actors in the process (often known as stakeholders); how to manage a requirements process and how to assess and improve its quality.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (3, 'EN_UK', 'Requirements Elicitation', 'Covers the sources of requirements (such as domain knowledge, stakeholders, business rules, etc.) and the techniques for collecting them effectively (such as interviews, scenarios, prototypes, meetings, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (4, 'EN_UK', 'Requirements Analysis', 'Covers how to classify requirements according to their type, importance, stability, or scope; and conceptual modeling (such as use cases, data models, state models, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (5, 'EN_UK', 'Requirements Specification', 'Covers the production of a set of documents on the requirements that can be systematically reviewed, evaluated, and approved. These documents are: the System Definition, the System Requirements Specification, and the Software Requirements Specification (derived from the system requirements).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (6, 'EN_UK', 'Requirements Validation', 'Covers the techniques for validating the requirements gathered, such as: peer review, prototyping, model validation, and acceptance testing.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (7, 'EN_UK', 'Practical Considerations', 'Covers the practical aspects of requirements; the iterative nature of the requirements process; the management of requirements changes (as a whole or in only some of their attributes); requirements traceability; and measurement of the weight of requirements in the event of a product change.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (8, 'EN_UK', 'Software Design Fundamentals', 'Covers the general concepts of design (such as objective, constraints, alternatives, context, etc.); the design process (difference between architectural and detailed design); and the principles of software design (such as abstraction, coupling, cohesion, decomposition, modularization, encapsulation, separation of interface and implementation, sufficiency, completeness, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (9, 'EN_UK', 'Key Issues in Software Design', 'Covers typical problems in software design such as concurrency: event control; data persistence; distribution of components and communication between them; error handling and fault tolerance; interaction and presentation; and security.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (10, 'EN_UK', 'Software Structure and Architecture', 'Covers the ways to organize software at various levels of abstraction from macro-architecture to detailed code design; the different architectural styles such as layered construction, distributed systems (Client-Server, Three-Tiers, Broker), interactive systems (Model-View-Controller, Presentation-Abstraction-Control), adaptable systems (microkernel or reflection), and others; Design Patterns (such as builder, factory, adapter; bridge, iterator, strategy, etc.); the decision-making aspect of the design process; and software program families and frameworks.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (11, 'EN_UK', 'User Interface Design', 'Covers effective communication with the software user; basic UI principles (ease of learning, familiarity, consistency, minimal surprise, diversity of use, etc.); dealing with common problems (user interaction and information presentation); typical interaction modes (question-and-answer, direct manipulation, menu selection, forms, command languages, natural language); how to present information (number of colors, color changes, color coding, visualization for people with special needs); the UI design process (user analysis, software prototyping, evaluation of the developed user interface); adapting the UI for localization and internationalization; and metaphors and conceptual models (such as using universally recognized icons for various common actions, such as using a scroll wheel to represent settings).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (12, 'EN_UK', 'Quality Analysis and Evaluation', 'Covers the quality attributes of design (such as portability, testability, robustness, performance, security, reusability, etc.); quality assessment techniques (such as third-party design review, formal static analysis - with mathematical models - or informal, simulation or prototyping); and software metrics for its size, structure, or quality.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (13, 'EN_UK', 'Software Design Notations', 'Covers the representation of design in different universally understood artifacts; structure descriptions (class and object diagrams, component diagrams, class responsibility cards, entity-relationship diagrams, interface description languages, etc.); and descriptions of behaviors and dynamics between components (activity diagrams, communication diagrams, data flow diagrams, decision tables and diagrams, sequence diagrams, state transition diagrams, pseudocode, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (14, 'EN_UK', 'Software Design Strategies and Methods', 'Covers how to design a system in one or more well-defined paradigms, such as Functional, Object-Oriented, Data-Structure-Centric, and Component-Centric; and general design strategies such as divide-and-conquer, top-down, bottom-up, etc.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (15, 'EN_UK', 'Software Design Tools', 'Covers tools that help create software design artifacts, typically software itself, which can, for example, create models and diagrams or enable quality analysis of models.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (16, 'EN_UK', 'Software Construction Fundamentals', 'Covers the fundamental concepts of software construction, which are: Minimization of Complexity, Anticipation of Changes, Construction for Verification, Reuse, and Construction Standards (relating to the conventions of the programming languages used, such as variable naming or indentation, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (17, 'EN_UK', 'Construction Management', 'Covers how to manage a construction process through Life Cycle Models (such as linear Waterfall methodologies or iterative Agile types); construction planning (the order of activities in a construction process - this is highly dependent on the construction model used); and how to measure what is created (statistics on code, scheduling, effort - again, different measures depend on the model chosen, e.g. effort is a typical measure of the scrum methodology).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (18, 'EN_UK', 'Practical Considerations', 'Covers the predominantly practical nature of construction; the need to make small-scale adjustments or modifications to the Design during construction; the languages needed to build software - such as configuration languages, scripting languages, programming languages, visual languages, etc.; the characteristics of coding itself (semantic constructs, control structures, error handling, organization of code into classes, ways of documenting code, etc.); construction tests (unit tests and integration tests); construction techniques for reuse by others (such as structuring libraries, using known design patterns); reuse of pre-existing software (use of libraries, public repositories, etc.); quality in construction (code testing, debugging, assertions, defensive programming, code reviews, etc.); and integration of code into a system (phase integration and incremental).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (19, 'EN_UK', 'Construction Technologies', 'Covers how to design and use APIs; typical problems of object-oriented paradigms (such as polymorphism and reflection); parameterized and generic types; assertions, interface programming, and defensive programming; error handling; executable models (creating a program by compiling an executable model of a modeling language); state-based and table-based programming; runtime configuration and internationalization; language grammar processing; concurrency primitives (semaphores, mutexes, monitors, etc.); middleware (software that provides services between the OS layer and the application layer); programming for distributed systems; construction of heterogeneous systems (such as Digital Signal Processors or Embedded Systems); performance analysis and tuning (choices of architectures, data structures, or algorithm selection); platform standards (e.g. J2EE vs J2SE); and test-driven development.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (20, 'EN_UK', 'Software Construction Tools', 'Covers all kinds of tools (typically also software) that help with software construction, such as IDEs (integrated development environments such as Eclipse, Netbeans, Visual Studio, etc.); GUI builders (now also part of modern IDEs); unit testing tools, and also code profiling tools, performance analysis tools, and slicing tools.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (21, 'EN_UK', 'Software Testing Fundamentals', 'Covers the fundamental concepts of testing; the distinction between fault, failure, error, and defect; test case selection; test effectiveness and objectives; testing for defect discovery; theoretical and practical limits of testing; impractical test scenarios; and the relationship of testing to other software lifecycle activities.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (22, 'EN_UK', 'Testing Levels', 'Covers the different levels at which software can be tested; by test target (unit testing vs integration testing vs system testing); by test objective (acceptance testing, installation testing, alpha and beta testing; reliability testing, regression testing, performance testing, security testing, stress testing, back-to-back testing, recovery testing, interface testing, configuration testing, and usability testing).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (23, 'EN_UK', 'Testing Techniques', 'Covers the different techniques used to find the largest number of possible failures; techniques based on the Engineer''s Intuition (ad hoc and exploratory testing); techniques based on the input domain (equivalent partitioning, combinatorial testing, boundary value testing, random testing); techniques based on code (code control flow testing, data flow testing); techniques based on failures (error guessing, mutation testing); techniques based on usage (typical operational profile testing, user observation heuristics); techniques based on models (decision tables, state machines; formal specifications, workflow models); techniques based on the nature of the application (web application vs mobile application, etc.); and combination of the described techniques.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (24, 'EN_UK', 'Testing Metrics', 'Covers the different metrics used to assess the quality of tests; program evaluation metrics (size metrics, typical failures for that type of software, fault density, reliability assessment, reliability growth models); and test execution evaluation metrics (total test coverage, fault introduction, mutation results).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (25, 'EN_UK', 'Testing Process', 'Covers how to organize testing activities, strategies, and techniques into a controlled process; practical considerations (creating test guides, process organizers, creating test documentation, test-driven development, creating an independent or internal test team, effort and test estimates, defining test termination, test reuse, and using a test pattern); and testing activities (test planning, test case generation, training environment development, systematic test execution, evaluation of test results, recording of results over time, defect tracking).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (26, 'EN_UK', 'Software Testing Tools', 'Covers the tools, typically also software, that help in the selection, execution, and evaluation of tests.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (27, 'EN_UK', 'Software Maintenance Fundamentals', 'Covers the basic concepts, terminology, and definitions of software maintenance; the specific role of a software maintenance engineer; maintenance needs (correcting faults, improving software design, adapting to new environments, etc.); software maintenance costs; software evolution as one of the purposes of maintenance; and the different categories of maintenance (corrective, adaptive, perfective, and preventive).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (28, 'EN_UK', 'Key Issues in Software Maintenance', 'Covers the main problems that a software maintenance manager faces, such as technical issues (limited understanding of the software in question, testing a system under maintenance, impact analysis of the proposed maintenance, degree of modifiability of a system in production); issues related to the management of the maintenance process (aligning the software maintenance process with the objectives of the responsible organization, managing and motivating maintenance managers, outsourcing maintenance); maintenance cost estimates; and maintenance metrics.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (29, 'EN_UK', 'Maintenance Process', 'Covers the life cycle of a maintenance phase (analyzing problems, implementing modifications, approving, discontinuing the product, migrating, etc.); and the activities exclusive to a maintenance engineer (analyzing the state of the software, support tasks, planning maintenance in terms of deadlines and scope of maintenance, managing the configuration of the software under maintenance, ensuring the quality of the software under maintenance).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (30, 'EN_UK', 'Maintenance Techniques', 'Covers the techniques used to successfully maintain and/or update software; understanding the software code; techniques for systematic software modification (such as refactoring); software reverse engineering techniques; software migration techniques; and software discontinuation techniques and policies.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (31, 'EN_UK', 'Software Maintenance Tools', 'Covers the tools that help with software maintenance, such as software slicing tools (which isolate the parts to be modified), static and dynamic code analyzers; data flow and dependency analyzers; and reverse engineering tools.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (32, 'EN_UK', 'Configuration Management Process Management', 'Covers how to organize configuration management; the constraints imposed on the process; typical planning of a configuration management process (who is responsible, what is the project schedule, what tools are to be used, what configurations to provide to third parties, the potential interactions between the software to be controlled and external ones); documentation of configurations and their management and use as guidelines in an organization; ensuring the correct application of the organization''s configurations either through the application of metrics, own tools, or simple audits.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (33, 'EN_UK', 'Software Configuration Identification', 'Covers the identification of the entities to be subject to configuration control, the relationships between them, how to define their versioning, rules for creating a baseline, policies for integrating a software item (a file, source code, visual resources, etc.) into configuration control; use and management of software libraries.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (34, 'EN_UK', 'Software Configuration Control', 'Covers the control of the normal flow of a software configuration change management process, such as change request, evaluation, and acceptance/rejection of the proposed change; implementation of the change and its tracking, and finally authorization of deviation (of the functionality to be changed) or waiver (idem).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (35, 'EN_UK', 'Software Configuration State Recording', 'Covers the recording and reporting of the evolution of the state of software configurations over time; how to capture the state of software items at each moment and how to report them to the organization.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (36, 'EN_UK', 'Software Configuration Audit', 'Covers the process of scrutinizing software configurations to ensure that their operation is aligned with the specified requirements (as a whole or at the level of a specific baseline), the planned architecture/design, and is aligned with the documentation.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (37, 'EN_UK', 'Software Release and Delivery Management', 'Covers how to organize the delivery of one or more software versions; how to execute a software build process (packaging, distributing different versions simultaneously, different production environments; delivering a complete software product (when to deliver based on the state of the software, what other deliverables are there - such as documentation and release notes).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (38, 'EN_UK', 'Software Configuration Management Tools', 'Covers the types of tools used to support effective configuration management, such as version control tools, build tools, customer issue support tools, development team workspace control tools, issue-tracking tools, etc.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (39, 'EN_UK', 'Initiation and Scope Definition', 'Covers how to determine and negotiate software requirements; feasibility analysis (assessing the ability to meet the requirements based on all the constraints that are subject to it - such as costs, technologies, human resources, policies, etc.); requirements review processes (what are the rules agreed with stakeholders to make any changes to the requirements).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (40, 'EN_UK', 'Software Project Planning', 'Covers how to plan a software project; what software lifecycle model to use (such as waterfall, spiral, or agile methods) and what tools to use to ensure its application; choice of deliverables (documentation, installable software, etc.); cost, effort, and timing estimates; allocation of human resources to tasks; risk management (identification, impact analysis, prioritization of risk factors, development of risk mitigation strategies, etc.); quality management (definition of quality metrics, ensuring the application of quality control throughout the project).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (41, 'EN_UK', 'Software Project Execution', 'Covers the application of planning to project development; application of the terms contracted with the software recipient (the client); application of the planned measurements and metrics; monitoring and adapting plans as the project progresses and in some cases abandoning the project; organizing and developing progress reports.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (42, 'EN_UK', 'Review and Evaluation', 'Covers the monitoring and periodic evaluation of the project against the objectives and customer satisfaction; determining customer satisfaction with the requirements; evaluation and review of the performance of the methods, tools, and resources applied in the project.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (43, 'EN_UK', 'Project Termination', 'Covers the completion of the project in its entirety or at a milestone; determining the scope of "termination" either in whole or in part (e.g. a pre-agreed number of implemented requirements can be a phase termination as well as all agreed requirements and the respective documentation can be the total termination); project termination activities (such as archiving documentation, how long to keep documentation, updating project statuses, preparing "Lessons Learned" documents, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (44, 'EN_UK', 'Software Engineering Measurement', 'Covers the application of measurements at the project, process, and work product levels; establishment and encouragement of measurements (what are the measurements, what is the scope, application of measurements by teams, etc.); measurement process planning; application of measurements in the processes with all relevant data collected and documented; evaluation of measurements and application of corrections or improvements if necessary.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (45, 'EN_UK', 'Software Engineering Management Tools', 'Covers the tools used to aid in the management of a software project (planning and monitoring the project status, risk management tools, communication tools, and measurement tools). These are also typically software.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (46, 'EN_UK', 'Software Process Definition', 'Covers the definition of what a software process is and what it comprises; required inputs, transformative work activities, and generated outputs; decomposition of the process into work units; roles and responsibilities; techniques, tools, and notations; software process management, its efficiency for a project, and the impacts of changing it to another.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (47, 'EN_UK', 'Software Life Cycles', 'Covers the different categories of software development life cycle models; categories of software processes, such as primary processes (for software development, operation, and maintenance), support processes (for configuration management, quality control, verification, and validation), organizational processes (training, infrastructure management, portfolio management, etc.), inter-project processes (transversal to several projects); software life cycle models (predictive or linear models such as the waterfall model vs iterative models such as the spiral model vs agile development models such as scrum); adaptation of existing software processes to the needs of the project, organization, or technological developments.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (48, 'EN_UK', 'Software Process Evaluation and Improvement', 'Covers the monitoring of process performance and its potential modification to better meet the specific needs of the project or organization; the different models and methods (qualitative ones that are subject to expert judgment or quantitative ones that are performed based on metrics); iterative process improvement models (such as the Plan-Do-Check-Act); process assessment and scoring systems.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (49, 'EN_UK', 'Software Process Metrics', 'Covers the different measures and measurement techniques used for software processes; measures of process efficiency (such as effort measures, man-hours, man-days, monetary costs of the process or process phase) or of the product itself; the quality of the measures to be used for processes and products; the use of software with process analysis models that allow testing various parameters of the processes used and determining possible improvement points; quantitative process measurement techniques (direct measurement of number of defects, man-hours, among others, and respective analysis by statistical methods) and qualitative techniques (such as questionnaires or interviews).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (50, 'EN_UK', 'Software Engineering Process Tools', 'Covers all kinds of tools related to software processes, such as BPMN diagrams, spreadsheets, UML diagrams, or even project management software.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (51, 'EN_UK', 'Modeling', 'Covers the general principles of software modeling, which are "Modeling the essentials", "Providing a perspective on the problem", and "Enabling effective communication"; the properties of models (completeness, consistency, and accuracy) and expression of models (concept of entity and relationship and their meanings according to the type of model); the syntax and semantics of modeling languages (textual or graphical); the concepts of precondition, postcondition, and invariants.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (52, 'EN_UK', 'Types of Models', 'Covers the different types of models used for software; information models (which identify and define a set of concepts, properties, relationships, and constraints on data entities); behavioral models (identify and define the functions of the software to be modeled, common examples are state machines, flow control models, and data flow models); structural models (define the logical or physical composition of its different parts, examples of which are class diagrams, object diagrams, and package diagrams).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (53, 'EN_UK', 'Model Analysis', 'Covers the techniques of critical inspection of models by the engineer; completeness analysis; consistency analysis; accuracy analysis; traceability between requirements, models, and implemented functionality; analysis of interaction between models and functionality.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (54, 'EN_UK', 'Software Engineering Methods', 'Covers the organized and systematic approaches to software development for a target computer; Heuristic Methods, which are based on experience and widely practiced by the industry (such as Structured Analysis methods that treat software as a hierarchical set of functions, Data Modeling methods that focus on the form and relationship of data for organizations whose main asset is precisely data, and Object-Oriented Methods, whose software is a set of objects that represent the real world, encapsulate data, and have behavior); Formal Methods that use a rigorous mathematical notation that allows software to be defined, refined, derived, and verified; Prototyping Methods whose purpose is to develop a small or incomplete part of the software (usually the least well understood) in order to refine it with critical input from the prototype; Agile Development Methods, characterized by short iterative developments with high participation of team members (methods such as Extreme Programming, scrum, or Cristal Clear).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (55, 'EN_UK', 'Software Quality Fundamentals', 'Covers the general principles of what defines quality for stakeholders; the installation of a quality-oriented work culture and ethic; the value of developing quality software and the impact it has on its costs; characteristics and quality models at the process and product level; quality improvement techniques (such as Plan-Do-Check-Act, kaizen, or Quality Function Deployment); concepts of software for critical systems and the impact that quality has on these systems.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (56, 'EN_UK', 'Software Quality Management Processes', 'Covers the processes that ensure that software products, services, and life cycle models meet organizational quality and stakeholder satisfaction goals; software quality assurance; quality verification and validation; review and audit of software processes and products.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (57, 'EN_UK', 'Practical Considerations', 'Covers the practical problems that have an impact on software quality: how requirements influence quality management, such as the type of system security, the physical environment, the project budget, the degree of dependence of the project (critical software implies a high quality process); the different types of software defects (computational error, human error, defect, fault, and failure); software quality management techniques, such as static techniques (source code analysis, design analysis, formal methods, etc.), dynamic techniques (code execution, simulation, debugging), software testing performed by individuals outside the development team to verify the software''s conformance to its specification; software quality measurement (using quality metrics, statistical analysis, trend analysis, etc.) with the purpose of having an overview of the product quality and its problematic parts.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (58, 'EN_UK', 'Software Quality Tools', 'Covers software quality process support tools, such as static code syntax and semantics analysis tools (such as compilers, Java Mission Control, etc.) and dynamic code analysis tools (such as debuggers).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (59, 'EN_UK', 'Professionalism', 'Covers the engineer''s adherence to codes of ethics, professional conduct, and standard practices established by their professional community; accreditation, certification, and licensing; codes of ethics and professional conduct defined by their professional community (such as the ACM and IEEE codes of ethics); professional societies and their function (e.g., the IEEE or the Order of Engineers); software engineering standards (e.g., the IEEE''s many software engineering standards); the economic impact of software; the various types of employment contracts and characteristics; legal issues (such as trademarks, intellectual property, fair use, cybercrime, copyright); the creation of documentation and its procedures; negotiations (of requirements, functionalities, contracts, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (60, 'EN_UK', 'Group Dynamics and Psychology', 'Covers issues related to teamwork and different groups of individuals such as clients or collaborators; issues related to teamwork (intellectual honesty, admitting ignorance, admitting mistakes, adaptability); factors that affect personal cognition (volume of data, complexity of problems, inability to express problems, inability to consult with others, etc.); how to deal with complex problems (teamwork, problem decomposition, etc.); how to interact with stakeholders; dealing with uncertainty and ambiguity in development problems; dealing with multiculturalism issues in teams or clients.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (61, 'EN_UK', 'Communication Skills', 'Covers the engineer''s oral and written communication skills; reading technical material, comprehension, and summarization; writing skills for documents relevant to software processes (requirements, software documentation, contracts, etc.); effective communication with teams or groups; skills to create and deliver presentations (of ideas, products, pitches, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (62, 'EN_UK', 'Economic Fundamentals', 'Covers the basic concepts of economics; finance and accounting; cost control; cash flow; decision-making processes (oriented to economics); concept of value; inflation and depreciation; taxation (taxes); time value of money (the fluctuation of the value of money over time); economic efficiency; effectiveness; definition of productivity.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (63, 'EN_UK', 'Lifecycle Economics', 'Covers the economic concepts applied to software activity throughout its lifecycle; concepts of product, project, program, portfolio, product lifecycle, project lifecycle, proposals and types of proposals; investment decisions; planning horizon (on the economic costs related to investments in a certain period, such as the lifetime of a software product and its economic impact on a company); concept of price and establishment of pricing policies; concept of cost and cost management; measurement of economic performance; earned value management (an economic technique that allows effective cost control by comparing the value already created with the projected budget and the cost schedule up to that point); product termination decisions based on economics; replacement decisions (replacing an outdated software with a new one instead of maintaining it) or discontinuation (of a product or activity as a whole).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (64, 'EN_UK', 'Risk and Uncertainty', 'Covers the concepts of economic objective, estimates and planning; estimation techniques (such as analogies, part-by-part estimation, statistical methods, etc.); reacting to uncertainty (such as using estimates from different areas, postponing final decisions, etc.); prioritization techniques; decision-making techniques under risk (such as decision trees, Monte Carlo analysis, etc.); decision-making techniques under uncertainty (such as Laplace''s Rule, Hurwicz''s Rule, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (65, 'EN_UK', 'Economic Analysis Methods', 'Covers the different economic analysis methods applicable depending on the type of organization, purpose or objectives of the software: decision analysis for profit-making organizations; minimum acceptable rate of return (investment selection criterion based on a minimum percentage return); return on investment (ratio of value gained to invested); return on capital employed; cost-benefit analysis; cost-effectiveness analysis; break-even analysis (equality between costs and revenues); business case (structured and consolidated presentation of a business proposal from different decision perspectives such as costs, benefits, risk, etc.); multi-attribute evaluation (at the level of other properties than just money); optimization analysis.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (66, 'EN_UK', 'Practical Considerations', 'The "Good Enough" principle (when the cost of adding a new feature - or investing in something non-essential - cannot be measured, this principle is an indicator of when the state of this feature is good enough to be delivered); frictionless markets (whenever there are barriers to perfect competition, be it transportation costs, establishments, protectionism and, particularly in software, internet non-neutrality or limitation of application sales in specific stores); ecosystems (when the qualities or attributes of a product are improved due to the existence of third parties that somehow interact with the product, such as an application that is integrated or configured to measure by an external company); offshoring (the development of an economic activity outside the organization''s operating territory for reasons of cost or quality deficits) and outsourcing (the execution of tasks for an organization by an external entity when these activities could be done by the organization).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (67, 'EN_UK', 'Problem Solving Techniques', 'Covers the definition of problem solving; problem formulation; problem analysis; designing a solution search strategy; problem solving using computer programs.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (68, 'EN_UK', 'Abstraction', 'Covers the concepts inherent in abstraction - generically defined as "the process and result of generalization by reducing information to a concept, a problem, or an observable phenomenon in order to allow focus on the big picture"; levels of abstraction; concept of encapsulation; hierarchy of abstractions.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (69, 'EN_UK', 'Programming Fundamentals', 'Covers the basic concepts of programming - the creation of computer programs using code; the programming process (design, writing, testing, debugging, and maintenance); the characteristics of different programming paradigms (unstructured, structured, procedural, imperative, object-oriented, functional, aspect-oriented, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (70, 'EN_UK', 'Programming Language Fundamentals', 'Covers the languages in which it is possible to write code in order to create programs for a computer; the syntax and semantics of a language; low-level languages, high-level languages; differences between declarative and imperative languages.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (71, 'EN_UK', 'Debugging Techniques and Tools', 'Covers the process of finding and fixing problems in written software; the different types of errors (syntax errors, logical errors, data errors, etc.); debugging techniques (one step at a time, using breakpoints, using watchpoints, etc.); debugging tools (such as debuggers or static code analysis tools).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (72, 'EN_UK', 'Data Structures and Representation', 'Covers the organization and forms of expression of data for use by programs; types of data structures (lists, queues, stacks, tables, hash tables, etc.); the different operations on different data structures (CRUD - create, read, update and delete, element finding operations, traversal, sorting).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (73, 'EN_UK', 'Algorithms and Complexity', 'Covers the organized writing of programs into algorithms - a strategy defined in a series of steps to solve a problem; attributes of algorithms (modularity, functionality, robustness, simplicity, etc.); algorithm analysis (worst-case analysis, average-case analysis, Big-O notation, etc.); algorithm design strategies (brute force, divide and conquer, greedy algorithms, dynamic programming, etc.); algorithm analysis strategies (step counting, asymptotic analysis, probabilistic analysis, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (74, 'EN_UK', 'Basic System Concepts', 'Covers the definition of a system; the properties of a system; emergent properties of a system; systems engineering and foundations of a computer system.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (75, 'EN_UK', 'Computer Organization', 'Covers the four levels of computer organization (macro-architectures, micro-architectures, logic circuits, and devices); digital systems (based on signals and binary code); digital logic (operations on binary numbers such as AND, OR, etc.); the expression of binary data in a computer; the central processing unit and its operation (CPU); memory organization; the operation of I/O devices.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (76, 'EN_UK', 'Compiler Fundamentals', 'Covers what a compiler is; the differences between compilers and interpreters; the compilation process.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (77, 'EN_UK', 'Operating System Fundamentals', 'Covers the concepts of an operating system; the tasks of an operating system; the different types of operating systems (batch, time-sharing systems, real-time systems, distributed operating systems, embedded systems, server operating systems, mobile operating systems, personal computer operating systems, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (78, 'EN_UK', 'Databases and Data Management', 'Covers the concepts of a database; entity and schema; the different database management systems (Oracle, MS SQL Server, Postgre, etc.); database query language - such as SQL - its features and how to use it; organization and manipulation of a database management system; data mining (the discovery of patterns and non-obvious relationships between data).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (79, 'EN_UK', 'Networking Fundamentals', 'Covers the different types of networks (personal area network, local area network, wide area network, internet, etc.); the basic concepts of networks (their components and the different functions of these); network protocols and standards (OSI stack and its components such as TCP/IP, UDP, Bluetooth, etc.); the Internet; the Internet of Things; virtual private networks (VPN).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (80, 'EN_UK', 'Parallel and Distributed Computing', 'Covers the basic concepts of parallel computing and distributed computing; the differences between these two paradigms; parallel computing models (shared memory, message passing, etc.); the main problems in distributed computing.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (81, 'EN_UK', 'Basic Human Usability Factors', 'Covers the typical problems that arise from the use of software by human users; considerations about I/O (such as how to show output to users, what is the most suitable format, what is the most pleasant way to present it, etc.); error messages (how to clearly explain what happened and how to solve it, what type of language, etc.); software robustness (how software tolerates incorrect use by people, how to recover in a way that does not discourage use by people, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (82, 'EN_UK', 'Basic Human Development Factors', 'Covers the common problems that arise from software being built and maintained by humans and how its writing affects these activities; how to structure code for easy understanding; how to write comments that make the work of present and future software owners easier.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (83, 'EN_UK', 'Secure Development and Maintenance', 'Covers the practices and rules followed in order to make software more secure in all its phases, both at the requirements level and at the construction and testing level.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (84, 'EN_UK', 'Sets, Relations, and Functions', 'Covers the mathematical and logical concepts about sets (collection of elements), their properties and operations (intersection, union, Cartesian product, etc.); about relations (associations between two sets of information); about mathematical functions (a relationship of known behavior that expresses the mapping between elements of domains).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (85, 'EN_UK', 'Basic Logic', 'Covers propositional logic, its terminology and laws (Boolean variables, Boolean operations, Identity Laws, Distributive Laws, De Morgan''s Laws, etc.): Predicate logic, its properties, terminology and laws.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (86, 'EN_UK', 'Proof Techniques', 'Covers the arguments that rigorously establish the truth of a statement; the methods of proving a theorem (proofs by contradiction, proofs by induction, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (87, 'EN_UK', 'Counting Fundamentals', 'Covers the actions that allow finding the number of elements in a finite set of elements and its rules (sum rule, product rule, etc.) and principles; inclusion-exclusion principle; definition of recursion, randomness, probability, permutation and combination.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (88, 'EN_UK', 'Graphs and Trees', 'Covers the different types of graphs (directed graphs, undirected graphs, etc.), their terminology (vertices, nodes, weight, edges, etc.) and operations (traversal, connections, etc.); the different types of trees, their properties and operations (traversals, additions, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (89, 'EN_UK', 'Discrete Probability', 'Covers the mathematical description of randomness, its terminology, properties and operations.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (90, 'EN_UK', 'Finite State Machines', 'Covers finite state machines (the mathematical abstraction composed of a finite number of states and the transitions between them), their terminology and operations.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (91, 'EN_UK', 'Grammars', 'Covers the grammars of formal languages, their terminology (vocabulary, sentence, terminals, symbol, etc.); the existing types of grammars (context-free grammar, context-sensitive grammar, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (92, 'EN_UK', 'Numerical Precision, Accuracy, and Errors', 'Covers the problems that arise in the representation of numbers; the finite nature of the precision of number representation on a computer; rounding techniques; significant digits; types of errors (relative, absolute).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (93, 'EN_UK', 'Number Theory', 'Covers numbers; the types of numbers (integer, rational, real, complex, imaginary, etc.); terminology and operations (division, mod, modulus); prime numbers, their usefulness and applications (such as cryptography).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (94, 'EN_UK', 'Algebraic Structures', 'Covers algebraic structures (defined as sets subject to some operations and satisfying a number of axioms); groups, their properties (associativity, identity, closure, etc.) and types (monoid, subgroup); rings (a set with two binary operations - usually addition and multiplication - where each operation combines two elements to form a third).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (95, 'EN_UK', 'Empirical Methods and Experimental Techniques', 'Covers how to propose solutions or solution models and conduct experiments to solve problems; creating controlled experiments (formulating hypotheses, creating preconditions, observing variables, relationship between results, etc.); observational study using contextualization; retrospective study, also known as historical study (analysis of historical data to find relationships between data and predict future events).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (96, 'EN_UK', 'Statistical Analysis', 'Covers the ability to achieve generalizable results by studying a limited population of samples of previous results (i.e. using statistics); the statistical concepts of sample, population, unit of analysis, random variable, event, distribution of variables, estimation, types of estimates, properties of estimators and statistical hypothesis testing; correlation and regression.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (97, 'EN_UK', 'Measurement', 'Covers the problem of measurement in software and its fundamental differences from other simpler measurement attributes; the concepts of levels (or scales) of measurement (nominal, ordinal, interval and ratio); direct versus indirect measures; reliability and validity of a measure; evaluation of reliability of measures.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (98, 'EN_UK', 'Engineering Design', 'Covers the challenges that the development of a structured solution to an engineering problem represents for the engineer, since in this case the design has nothing to do with its traditional concept - the graphical representation - but rather with the planning or architecture of a solution to a problem, its process and its phases (definition of a problem, obtaining relevant information, generation of multiple solutions, analysis and selection of a solution, implementation of the solution).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (99, 'EN_UK', 'Modeling, Simulation, and Prototyping', 'Covers the concepts and techniques of modeling (the process of representing part or all of a system using abstractions) and its different types (iconic, analogical and symbolic); the concept of simulation (conducting experiments on models in order to improve understanding of the model) and its construction; the concept of prototype (an initial, partial or incomplete representation of a system) and its use.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (100, 'EN_UK', 'Standards', 'Covers the concept of standard (requirement, specification, guide or characteristic that must be observed by engineers so that products, processes and materials have acceptable levels of quality); the different types of standards that exist; measurable compliance with standards; the different entities that produce international engineering standards.');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (101, 'EN_UK', 'Root Cause Analysis', 'Covers the process of investigating and identifying how and why an undesirable event occurred and its root cause (i.e. what caused the event); the main techniques for conducting this type of analysis (review methods, present state vs desired state diagrams, third party review, failure trees, etc.); the tools to aid the analysis (cause and effect diagrams, histograms, Pareto charts, etc.).');
INSERT INTO TopicTranslations(topic, language, name, description) VALUES (102, 'EN_UK', 'None of the Knowledge Areas', 'For any topic in the program that does not fit into any of the Knowledge Areas.');
