INSERT INTO Degrees(id, acronym, image, school, year) VALUES (5, 'MIEI', 'http://images.com/miei_um.jpeg' , 5, 2017);
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (1, 'PT_PT' , 'Mestrado Integrado em Engenharia Informática e Computação', 'O ensino da Engenharia Informática e Computação foi concebido na FEUP como requerendo um ciclo de formação e aprendizagem completo de cinco anos integrados, correspondendo aos 1º e 2º ciclos da implementação do Processo de Bolonha e conferindo de imediato o grau de Mestre.\n\nNo final dos três primeiros anos (1º ciclo) do MIEIC os alunos estarão providos de uma sólida formação de base, abrangendo as competências científicas e de engenharia essenciais, mas também os conhecimentos fundamentais de banda larga nas diversas áreas da informática, embora sem qualquer especialização. Este primeiro degrau é visto sobretudo como possibilitador de mobilidade de e para outras escolas nacionais e europeias.\n\nNo final dos cinco anos do curso os diplomados terão uma formação avançada em Engenharia Informática e Computação, podendo ter escolhido uma área de especialização ou manter um leque mais alargado de interesses, mercê de uma ampla oferta de opções, configurável individualmente, contida no plano de estudos. As possíveis especializações incluem actualmente:\n\n- Engenharia de Software e Sistemas de Informação (2 sub-áreas com estes nomes)\n\n-Redes e Tecnologias de Informação (sub-áreas de Tecnologias da Internet e de Infra-estruturas Informáticas)\n\n-Sistemas Inteligentes e Multimédia (2 sub-áreas com estes nomes)');
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (2, 'PT_PT' , 'Licenciatura em Engenharia Informatica e Mestrado em Engenharia Informática', 'O curso de licenciatura em Engenharia Informática tem como objectivo principal fornecer aos futuros licenciados os princípios básicos, teorias, métodos e práticas da Engenharia Informática e da Computação de forma a que possam dominar todos os conceitos para a sua intervenção na sociedade ou complementar a sua formação com o 2º ciclo seguindo a vida prática ou a carreira científica. Para a concretização deste objectivo geral, o ciclo de estudos pretende conciliar os seguintes objectivos específicos: instrumentais, sistémicas e interpessoais.\n\n
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (3, 'PT_PT' , 'Licenciatura em Engenharia Informatica e de Computadores e Mestrado em Engenharia Informática e de Computadores', 'A licenciatura em Engenharia Informática e de Computadores (LEIC) é uma das maiores licenciaturas do Técnico e tem já provas dadas de sucesso no mercado de emprego, tanto a nível nacional como internacional. Os diplomados da LEIC são reconhecidos como profissionais competentes com conhecimentos sólidos a nível da engenharia informática e fácil adaptabilidade à evolução constante das tecnologias de informação.\n\nNo final da LEIC o aluno poderá especializar-se numa das áreas oferecidas no  Mestrado em Engenharia Informática e Computadores do Técnico, num outro curso afim, ou ainda entrar no mercado de trabalho.\n\nO Mestrado em Eng. Informática e de Computadores está alicerçado na forte formação de base dada pelo primeiro ciclo, oferece especializações atractivas para os alunos e para o mercado servindo ainda como base para os alunos que pretendam ingressar num Programa de Doutoramento, e cria uma base sólida para a intervenção do Departamento de Engenharia Informática do IST na formação ao longo da vida, que representará uma componente cada vez mais importante do mercado da educação avançada.\n\nNo final do Mestrado em Engenharia Informática e de Computadores os alunos poderão entrar no mercado de trabalho. Tendo em conta as especializações acima indicadas, os alunos têm uma gama alargada de competências profissionais específicas, das quais se destacam:\n
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (4, 'PT_PT' , 'Licenciatura em Engenharia Informatica + Mestrado em Engenharia Informática', 'A Licenciatura em Engenharia Informática da Universidade de Aveiro tem por finalidade fornecer uma formação sólida em engenharia informática, área que se tem vindo a revelar como o principal motor de desenvolvimento nas economias avançadas.\n\nMais concretamente, esta licenciatura pretende fornecer as seguintes competências:\n
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (5, 'PT_PT' , 'Mestrado Integrado em Engenharia Informática', 'O Mestrado Integrado em Engenharia Informática (MiEI) é um curso integrado do primeiro e segundo
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (1, 'EN_UK', 'Integrated Master''s Degree in Computer Engineering and Computing', 'The teaching of Computer Engineering and Computing at FEUP was designed as a complete five-year integrated training and learning cycle, corresponding to the 1st and 2nd cycles of the Bologna Process implementation and immediately awarding the Master''s degree.
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (2, 'EN_UK', 'Bachelor''s Degree in Computer Engineering and Master''s Degree in Computer Engineering', 'The main objective of the Bachelor''s Degree in Computer Engineering is to provide future graduates with the basic principles, theories, methods and practices of Computer Engineering and Computing so that they can master all the concepts for their intervention in society or complement their training with the 2nd cycle following practical life or the scientific career. To achieve this general objective, the study cycle aims to reconcile the following specific objectives: instrumental, systemic and interpersonal.
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (3, 'EN_UK', 'Bachelor''s Degree in Computer Engineering and Computer Science and Master''s Degree in Computer Engineering and Computer Science', 'The Bachelor''s Degree in Computer Engineering and Computer Science (LEIC) is one of the largest undergraduate degrees at Técnico and has already proven its success in the job market, both nationally and internationally. LEIC graduates are recognized as competent professionals with solid knowledge in computer engineering and easy adaptability to the constant evolution of information technologies.
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (4, 'EN_UK', 'Bachelor''s Degree in Computer Engineering + Master''s Degree in Computer Engineering', 'The Bachelor''s Degree in Computer Engineering at the University of Aveiro aims to provide a solid education in computer engineering, an area that has been revealed as the main engine of development in advanced economies.
INSERT INTO DegreeTranslations(degree, language, name, description) VALUES (5, 'EN_UK', 'Integrated Master''s Degree in Computer Engineering', 'The Integrated Master''s Degree in Computer Engineering (MiEI) is an integrated course of the first and second cycles of higher education, taught at the University of Minho, with a duration of 5 academic years, 10 academic semesters, 40 weeks of full-time study per year and a total of 300 ECTS.
