INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (1, 'PT_PT', 'Matemática Discreta' , 'A Lógica constitui a base de qualquer raciocínio científico e essa é a razão primeira da sua inclusão no 1º ano do curso. Para além disso, no caso da Engenharia Informática, a Lógica tem um interesse directo operacional em múltiplas dimensões da profissão.');
