INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (4, 'PT_PT', 'Análise Matemática' , 'Aquisição de conhecimentos teóricos e práticos sobre cálculo diferencial e integral em R que possibilitem a aplicação das ferramentas básicas da análise matemática ao tratamento e resolução dos problemas mais adaptados ao perfil do curso.\nCapacitar o estudante para a inovação, complementando os conhecimentos de forma a desenvolver soluções para resolução de novas questões.');
