INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (5, 'PT_PT', 'Álgebra' , 'Esta disciplina tem dois objectivos fundamentais: por um lado, tratando-se de uma disciplina propedêutica tem um carácter didáctico/científico, promovendo o desenvolvimento do raciocínio lógico e de métodos de análise e, por outro, visa introduzir e desenvolver em termos teóricos um conjunto de conceitos que serão ferramentas essenciais para apoio às disciplinas mais específicas da Engenharia.');
