INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (155, 'PT_PT', 'Cinemática: Resolução de equações simples usando o método de separação de variáveis');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (156, 'PT_PT', 'Cinemática vectorial: Identificação dos graus de liberdade de um sistema mecânico e resolução de sistemas de equações de movimento');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (157, 'PT_PT', 'Movimento curvilíneo: Vectores tangente e normal. Aceleração centrípeta. Curvatura da trajectória. Cinemática dos corpos rígidos');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (158, 'PT_PT', 'Mecânica vectorial: Tipos de forças presentes nos sistemas mecânicos. Trabalho e energia');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (159, 'PT_PT', 'Dinâmica dos corpos rígidos: Adição de forças. Momento de uma força. Momento de inércia. Equações do movimento plano dos corpos rígidos');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (160, 'PT_PT', 'Trabalho e energia: Relações entre trabalho e energia. Forças conservativas e dissipativas');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (161, 'PT_PT', 'Sistemas dinâmicos: Espaço de fase. Equilíbrio estável e instável. Retratos de fase. Sistemas conservativos');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (162, 'PT_PT', 'Mecânica lagrangiana: Coordenadas, velocidades e forças generalizadas. Equações de Lagrange. Multiplicadores de Lagrange');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (163, 'PT_PT', 'Sistemas lineares: Osciladores harmónicos. Classificação dos pontos de equilíbrio. Valores próprios e vectores próprios. Análise de estabilidade');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (164, 'PT_PT', 'Sistemas não lineares: Pêndulos. Aproximação linear. Matriz jacobiana. Espaços de fase com várias dimensões');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (165, 'PT_PT', 'Ciclos limite e sistemas de duas espécies: Oscilador de Van der Pol. Sistemas predador presa. Evolução e coexistência de duas espécies');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (166, 'PT_PT', 'Sistemas caóticos: Comportamento asssimptótico, atractores estranhos e sistemas caóticos');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (155, 'EN_UK', 'Kinematics: Solving simple equations using the separation of variables method');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (156, 'EN_UK', 'Vectorial kinematics: Identifying the degrees of freedom of a mechanical system and solving systems of motion equations');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (157, 'EN_UK', 'Curvilinear motion: Tangent and normal vectors. Centripetal acceleration. Curvature of the trajectory. Kinematics of rigid bodies');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (158, 'EN_UK', 'Vectorial mechanics: Types of forces present in mechanical systems. Work and energy');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (159, 'EN_UK', 'Dynamics of rigid bodies: Addition of forces. Moment of a force. Moment of inertia. Equations of the plane motion of rigid bodies');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (160, 'EN_UK', 'Work and energy: Relationships between work and energy. Conservative and dissipative forces');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (161, 'EN_UK', 'Dynamical systems: Phase space. Stable and unstable equilibrium. Phase portraits. Conservative systems');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (162, 'EN_UK', 'Lagrangian mechanics: Generalized coordinates, velocities, and forces. Lagrange equations. Lagrange multipliers');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (163, 'EN_UK', 'Linear systems: Harmonic oscillators. Classification of equilibrium points. Eigenvalues and eigenvectors. Stability analysis');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (164, 'EN_UK', 'Nonlinear systems: Pendulums. Linear approximation. Jacobian matrix. Multidimensional phase spaces');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (165, 'EN_UK', 'Limit cycles and two-species systems: Van der Pol oscillator. Predator-prey systems. Evolution and coexistence of two species');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (166, 'EN_UK', 'Chaotic systems: Asymptotic behavior, strange attractors and chaotic systems');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (10, 1, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (10, 'PT_PT', 'Complementos de Matemática' , 'A unidade curricular tem dois objectivos fundamentais: por um lado, tratando-se de uma unidade curricular propedêutica tem um carácter didáctico/científico, promovendo o desenvolvimento do raciocínio lógico e de métodos de análise e, por outro, visa introduzir e desenvolver em termos teóricos um conjunto de conceitos que serão ferramentas essenciais para apoio às restantes unidades curriculares.');
