INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (169, 'PT_PT', 'Gradientes: Diferenciabilidade e gradiente, Gradientes e derivadas direccionais, Teorema do valor médio, Regras de derivação em cadeia, Valores máximos e mínimos, Diferenciais');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (170, 'PT_PT', 'Integrais Duplos e Triplos: Integral duplo sobre uma região, Integrais duplos usando coordenadas polares, Integrais triplos, Coordenadas cilíndricas, Coordenadas esféricas, Jacobianos');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (171, 'PT_PT', 'Integrais de Linha e Integrais de Superfície: Integrais de linha, Propriedades, Integrais de linha em relação ao comprimento de arco, Teorema de Green, Parametrização de superfícies, Área de uma superfície, Integrais de superfície, Divergência e rotacional, Teorema da divergência, Teorema de Stokes');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (167, 'EN_UK', 'Vector Functions: Properties, Curves, Arc Length, Curvature');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (168, 'EN_UK', 'Functions of Several Variables: Quadric Surfaces, Level Curves and Level Surfaces, Partial Derivatives, Limits and Continuity');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (169, 'EN_UK', 'Gradients: Differentiability and Gradient, Gradients and Directional Derivatives, Mean Value Theorem, Chain Rule, Maxima and Minima, Differentials');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (170, 'EN_UK', 'Double and Triple Integrals: Double integral over a region, Double integrals using polar coordinates, Triple integrals, Cylindrical coordinates, Spherical coordinates, Jacobians');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (171, 'EN_UK', 'Line Integrals and Surface Integrals: Line integrals, Properties, Line integrals with respect to arc length, Green''s theorem, Parameterization of surfaces, Area of a surface, Surface integrals, Divergence and curl, Divergence theorem, Stokes'' theorem');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (11, 1, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (11, 'PT_PT', 'Microprocessadores e Computadores Pessoais' , 'A unidade curricular de Microprocessadores e Computadores Pessoais tem por objectivo desenvolver, combinar e aplicar de forma integrada conceitos das áreas de Arquitectura de Computadores e de Linguagens de Programação. Por um lado, a unidade curricular explora as relações entre o conjunto de instruções do CPU e a programação de baixo nível (linguagem "assembly"). Por outro lado, reconhecendo que a arquitectura de computadores pessoais modernos vai muito para além da arquitectura do CPU, a unidade curricular abordará a arquitectura geral de um PC, incluindo o subsistema de armazenamento de dados e outros periféricos.');
