INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (12, 'PT_PT', 'Teoria da Computação' , 'Preparar os estudantes em tópicos relacionados com teoria da computação, com um ênfase especial em tópicos relacionados com linguagens formais.\nCapacitar os estudantes para que estes sejam capazes de expressar problemas computacionais usando linguagens formais, autómatos e máquinas de Turing.');
