INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (13, 'PT_PT', 'Métodos Numéricos' , 'A disciplina pretende em primeiro lugar fornecer aos estudantes competências sólidas no cálculo numérico. Será organizada por tópicos, cobrindo os problemas de representação numérica, a resolução de equações e sistemas de equações algébricas e diferenciais, a integração definida, a optimização não linear e o ajuste de curvas.');
