INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (14, 'PT_PT', 'Física II' , 'Esta unidade curricular visa dotar os estudantes com conhecimentos básicos de electromagnetismo e processamento de sinais. A abordagem é experimental, com recurso a experiências simples que os estudantes podem realizar durante as aulas teórico-práticas para consolidar os conhecimentos teóricos e adquirir experiência no uso dos instrumentos de medição. O Sistema de Computação Algébrica (CAS) usado na unidade curricular Física 1 é também aproveitado para facilitar a resolução de problemas e para visualizar campos eléctricos e magnéticos.');
