INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (18, 'PT_PT', 'Concepção e Análise de Algoritmos' , 'Esta Unidade Curricular tem por objectivo complementar e aprofundar os conhecimentos assimilados nas disciplinas de "Programação" e de "Algoritmos e Estruturas de Dados", nomeadamente pela introdução de técnicas de concepção e implementação de algoritmos eficientes para a resolução de diferentes tipos de problemas, assim como a sua análise e avaliação.');
