INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (21, 'PT_PT', 'Laboratório de Programação Orientada por Objectos' , 'Desenvolver e aprofundar as competências de programação orientada por objectos, numa linguagem orientada por objectos moderna (Java), representativa das linguagens utilizadas para desenvolvimento de software aplicacional.\nDesenvolver competências de desenho orientado por objectos, usando a linguagem UML e aplicando bons princípios e padrões de desenho.\nAprender a desenvolver aplicações com interface gráfica e a utilizar bibliotecas extensas.\nAdquirir o hábito de seguir boas práticas de desenvolvimento de software (desenvolvimento iterativo, testes unitários, "debugging", "refactoring", SCV, programação em pares, etc.).');
