INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (22, 'PT_PT', 'Programação em Lógica' , 'Adquirir familiaridade com os paradigmas da Programação em Lógica e da Programação com Restrições. Desenvolver as capacidades de raciocínio abstracto e de representação de problemas de forma declarativa.');
