INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (301, 'PT_PT', 'Fundações da Programação em Lógica: Lógica proposicional. Lógica de predicados. Regras de inferência. Cláusulas de Horn. Unificação. Resolução. Forma normal conjuntiva.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (302, 'PT_PT', 'Fundações da Programação em Lógica: Conceito de programação em lógica. Origens do Prolog.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (303, 'PT_PT', 'Conceitos da Programação em Lógica: Cláusulas. Predicados. Factos. Perguntas. Regras. Variáveis lógicas. Instanciação.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (304, 'PT_PT', 'Conceitos da Programação em Lógica: Programação em lógica e bases de dados. Recursividade. Listas. Árvores. Expressões simbólicas.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (305, 'PT_PT', 'Conceitos da Programação em Lógica: Execução de um programa em lógica. Algoritmo da unificação. Interpretador abstracto. Traçados. Árvores de pesquisa. Negação.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (306, 'PT_PT', 'A Linguagem Prolog: Modelo de execução. Backtracking. Ordem das regras e dos objectivos. Terminação.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (307, 'PT_PT', 'A Linguagem Prolog: Aritmética. Iteração. Inspecção de estruturas. Predicados meta-lógicos. Controlo: cuts e negação. Predicados extra-lógicos.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (308, 'PT_PT', 'Técnicas Avançadas de Programação em Prolog: Programação não determinística. Estruturas de dados incompletas. Meta-interpretadores. Técnicas de pesquisa.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (309, 'PT_PT', 'Programação em Lógica com Restrições: Restrições. Satisfação de restrições. Restrições em domínios finitos.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (310, 'PT_PT', 'Programação em Lógica com Restrições: Programação em lógica com restrições (PLR). Controlo da pesquisa. Ordenação de variáveis e valores. Modelização de problemas em PLR. Programação com restrições no SICStus Prolog.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (301, 'EN_UK', 'Foundations of Logic Programming: Propositional logic. Predicate logic. Inference rules. Horn clauses. Unification. Resolution. Conjunctive normal form.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (302, 'EN_UK', 'Foundations of Logic Programming: Concept of logic programming. Origins of Prolog.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (303, 'EN_UK', 'Concepts of Logic Programming: Clauses. Predicates. Facts. Queries. Rules. Logical variables. Instantiation.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (304, 'EN_UK', 'Concepts of Logic Programming: Logic programming and databases. Recursion. Lists. Trees. Symbolic expressions.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (305, 'EN_UK', 'Concepts of Logic Programming: Execution of a logic program. Unification algorithm. Abstract interpreter. Traces. Search trees. Negation.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (306, 'EN_UK', 'The Prolog Language: Execution model. Backtracking. Order of rules and goals. Termination.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (307, 'EN_UK', 'The Prolog Language: Arithmetic. Iteration. Structure inspection. Meta-logical predicates. Control: cuts and negation. Extra-logical predicates.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (308, 'EN_UK', 'Advanced Prolog Programming Techniques: Non-deterministic programming. Incomplete data structures. Meta-interpreters. Search techniques.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (309, 'EN_UK', 'Constraint Logic Programming: Constraints. Constraint satisfaction. Constraints over finite domains.');
INSERT INTO DegreeClassTopicTranslations(degreeClassTopic, language, description) VALUES (310, 'EN_UK', 'Constraint Logic Programming: Constraint logic programming (CLP). Search control. Variable and value ordering. Modeling problems in CLP. Constraint programming in SICStus Prolog.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (23, 3, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (23, 'PT_PT', 'Laboratório de Aplicações com Interface Gráfica' , 'Esta unidade curricular tem por objectivo principal desenvolver um conjunto de competências práticas, algumas já tratadas em unidades curriculares anteriores num contexto mais teórico. Foca-se em trabalhos práticos de desenvolvimento, tendencialmente multidisciplinares, nomeadamente nas áreas de Computação Gráfica e Interfaces, de Programação em Lógica e de Sistemas Operativos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (24, 3, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (24, 'PT_PT', 'Engenharia de Software' , 'No final da unidade curricular, os estudantes deverão ser capazes de:\n- descrever os princípios, conceitos e práticas da engenharia de software e do ciclo de vida do software;\n- conhecer e saber aplicar as técnicas e ferramentas necessárias para executar e gerir as várias actividades do processo de desenvolvimento de software de qualidade;\n- explicar os métodos e processos de construção de diferentes tipos de sistemas de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (25, 3, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (25, 'PT_PT', 'Redes de Computadores' , 'Redes de Computadores (RCOM) é a unidade curricular que introduz os estudantes no domínio de conhecimento das redes de comunicações. Os conceitos fundamentais endereçados em RCOM incluem os seguintes: canais de comunicação e controlo da ligação de dados, modelos de erro e atraso, comunicações multi-acesso, encaminhamento, controlo de fluxo e controlo de congestionamento. As principais tecnologias discutidas em RCOM são a Ethernet comutada, a Wireless LAN 802.11, a Internet e a pilha de comunicações TCP/IP. As implementações e projectos a desenvolver em RCOM incluem um protocolo de ligação de dados com controlo de fluxo, uma aplicação a funcionar em modo cliente, e a configuração e teste de redes Ethernet e IP.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (26, 3, 2, 7.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (26, 'PT_PT', 'Linguagens e Tecnologias Web' , 'Pretende-se, nesta disciplina, que os alunos desenvolvam competências nas linguagens e tecnologias WEB mais significativas, no contexto tecnológico actual, ou que foram determinantes no processo evolutivo da WEB.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (27, 3, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (27, 'PT_PT', 'Laboratório de Bases de Dados e Aplicações Web' , 'A unidade curricular de LBAW tem como objectivo sedimentar as matérias expostas nas unidades curriculares de bases de dados e linguagens e tecnologias web. Esta unidade curricular oferece uma perspectiva prática sobre duas áreas centrais da engenharia informática.\nNesta unidade curricular pretende-se dotar os estudantes da capacidade de projectar e desenvolver sistemas de informação acessíveis através da web e suportados por sistemas de gestão de bases de dados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (28, 3, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (28, 'PT_PT', 'Inteligência Artificial' , 'Esta unidade curricular apresenta um conjunto de assuntos nucleares para a área dos Sistemas Inteligentes.\nOs seu objectivos são:\n- Saber o que caracteriza e distingue a IA e qual a sua aplicabilidade.\n- Saber como Representar, Adquirir, Manipular e Aplicar Conhecimento usando Sistemas Computacionais.\n- Desenvolver um pequeno projecto usando técnicas de IA.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (29, 3, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (29, 'PT_PT', 'Compiladores' , 'Fornecer os conceitos que permitam:\n- compreender as fases de compilação de linguagens, em especial das linguagens imperativas e orientada por objectos;\n- especificar a sintaxe e semântica de uma linguagem de programação;\n- compreender e utilizar as estruturas de dados e os algoritmos principais usados na implementação de compiladores.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (30, 3, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (30, 'PT_PT', 'Sistemas Distribuídos' , 'Esta unidade curricular tem dois objectivos fundamentais:\ndotar os estudantes com conhecimentos teóricos sobre sistemas distribuídos para que possam tomar decisões correctas quando confrontados com a necessidade de implementar um sistema desta natureza;\ndotar os estudantes com conhecimentos práticos para que possa desenvolver e tirar partido dos sistemas distribuídos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (31, 3, 2, 4.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (31, 'PT_PT', 'Proficiência Pessoal e Interpessoal' , 'Identificar e adquirir os conhecimentos essenciais para saber como maximizar progressivamente as condições de êxito do desempenho pessoal e interpessoal.Ter consciência da importância de saber utilizar competências complementares (soft skills) e de realizar mudanças evolutivas de forma a desenvolver um perfil profissional que contribua para melhorar continuamente a futura atividade do engenheiro informático.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (32, 4, 1, 4.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (32, 'PT_PT', 'Sistemas de Informação' , 'Interpretar, Classificar e Explicar:\n- Processo de vendas e Procurement gerido por um ERP;\n- a forma como o SI contribui para a Construção de Valor numa organização, com especial foco no ERP e em outros com o qual o ERP se integra (e.g.: sistema de execução fabril e gestão de armazém);\n- Tendências tecnológicas e o seu impacto nos sistemas de informação empresariais (Cloud Computing, Big Data, a nova Internet).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (33, 4, 1, 7.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (33, 'PT_PT', 'Laboratório de Desenvolvimento de Software' , 'Objectivos: dotar os alunos para serem capazes de definir e aplicar um processo de desenvolvimento de software, utilizando práticas ágeis, ferramentas tipicas de desenvolvimento de software, em equipa.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (34, 4, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (34, 'PT_PT', 'Gestão de Empresas' , 'A unidade curricular tem por objectivo desenvolver competências na área da Gestão, integrando uma sólida componente teórica, baseada na apresentação e discussão de conceitos e metodologias, com a sua aplicação à análise e à discussão de casos de estudo.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (35, 4, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (35, 'PT_PT', 'Agentes e Inteligência Artificial Distribuída' , 'A unidade curricular assume-se como de Engenharia (evidenciando apenas a teoria essencial para se entender as aplicações práticas) e proporciona uma perspectiva global das técnicas associadas à especificação e desenvolvimento de Agentes computacionais bem como realça a sua relevância prática apresentando exemplos de aplicação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (36, 4, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (36, 'PT_PT', 'Métodos Formais em Engenharia de Software' , 'Desenvolver as capacidades de abstracção de forma a descrever o que o sistema deve fazer e não a maneira de o fazer. Estar familiarizados com os métodos formais e forma como eles podem contribuir para aumentar a qualidade dos sistemas de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (37, 4, 2, 4.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (37, 'PT_PT', 'Investigação Operacional' , 'Dotar os alunos com competências para:\n- identificar problemas de decisão;\n- aplicar as várias fases de resolução de um problema de decisão, em particular, a definição e estruturação de problemas, a construção de modelos e a utilização de métodos quatitativoas para a obtenção da solução;\n- analisar de forma crítica a solução obtida;\n- perceber a importância do papel de agente de mudança nas organizações.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (38, 4, 2, 7.5, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (38, 'PT_PT', 'Laboratório de Gestão de Projectos' , 'No final da Unidade Curricular os estudantes deverão ser capazes de:\n- Identificar a necessidade de recurso à gestão de projectos bem como a importância do seu enquadramento estratégico;\n- Identificar os diferentes referenciais normativos e metodologias para a gestão de projectos;\n- Identificar e definir as áreas de processo e variáveis da gestão de projectos;\n- Aplicar metodologias e ferramentas de gestão de projecto e trabalho em equipa;\n- Aplicar conceitos e ferramentas de gestão da qualidade a projectos;\n- Identificar e gerir os riscos inerentes a um projectco.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (39, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (39, 'PT_PT', 'Arquitectura de Sistemas de Software' , 'A disciplina de Arquitecturas de Sistemas de Software tem como objectivo principal introduzir os conceitos de arquitecturas de software, padrões de desenho e tópicos directamente relacionados, tais como o de componentes de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (40, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (40, 'PT_PT', 'Arquitectura e Gestão de Redes e Sistemas' , 'Esta unidade curricular tem como objectivo que os estudantes adquiriram competências no planeamento e gestão integrada de infra-estruturas de rede, sistemas e serviços.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (41, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (41, 'PT_PT', 'Computação Paralela' , 'Aquisição de conhecimentos conducentes à utilização simultânea de várias unidades de processamento num sistema de computação. Construção de bases sólidas sobre arquitecturas paralelas, paralelização de algoritmos, modelos de programação, sincronização de processos e medidas de desempenho, através do desenvolvimento de programas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (42, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (42, 'PT_PT', 'Comunicações Móveis' , 'Nesta UC são discutidos os aspectos fundamentais do funcionamento de redes de comunicações móveis, as tecnologias de comunicações móveis mais importantes, e técnicas de engenharia e de projecto de redes de comunicações móveis.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (43, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (43, 'PT_PT', 'Desenvolvimento de Jogos de Computador' , 'O objectivo desta unidade curricular é transmitir o conhecimento sobre o processo de desenvolvimento de jogos de computador, com ênfase particular nos aspectos relacionados com a programação dos diversos módulos que compõem um motor de jogo.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (44, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (44, 'PT_PT', 'Engenharia de Requisitos de Sistemas de Software' , '');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (45, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (45, 'PT_PT', 'Gestão de Operações e Logística' , 'Dotar os alunos com competências para planear, gerir e levar a cabo um processo de análise e especificação de requisitos de um sistema de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (46, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (46, 'PT_PT', 'Linguagens de Anotação e Processamento de Documentos' , 'Esta unidade curricular foca-se na obtenção de linguagens de anotação para um dado domínio e na realização de aplicações baseadas em linguagens de anotação e tem por objectivos:\n- Sensibilizar os estudantes para as múltiplas aplicações não triviais das linguagens de anotação;\n- Familiarizar os estudantes com as tecnologias de processamento e armazenamento de informação semi-estruturada;\n- Aplicar as tecnologias de anotação na definição de uma linguagem e na resolução de um problema com recurso ao desenvolvimento de uma aplicação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (47, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (47, 'PT_PT', 'Marketing' , 'A unidade curricular tem por objectivo desenvolver competências na área da Gestão de Marketing, integrando uma sólida componente teórica, baseada na apresentação e discussão de conceitos e metodologias, com a sua aplicação ao desenvolvimento de um plano de marketing e à discussão de casos de estudo.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (48, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (48, 'PT_PT', 'Modelação e Simulação de Sistemas' , 'Os objectivos da disciplina são:\n- Apresentar os conceitos básicos de modelação e simulação (computacional) e as suas diversas áreas de aplicação;\n- Apresentar as principais fases do ciclo de vida de um projecto de simulação, desde a fase de modelação até a análise e aplicação dos resultados;\n- Apresentar os principais tipos de modelos e a sua adequação ao tratamento de diferentes problemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (49, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (49, 'PT_PT', 'Multimédia e Novos Serviços' , 'São objectivos da unidade curricular:\na) apresentar os conceitos, os serviços e as aplicações multimédia e hipermédia;\nb) apresentar as técnicas de codificação e representação da informação multimédia;\nc) introduzir as ferramentas de desenvolvimento de aplicações; d) desenvolver aplicações.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (50, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (50, 'PT_PT', 'Segurança em Sistemas Informáticos' , 'Esta Unidade Curricular tem por âmbito o estudo da problemática de segurança de sistemas Informáticos, de forma a fornecer aos estudantes um painel básico dos conceitos e das ameaças e defesas de uma utilização abusiva e destrutiva de tais sistemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (51, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (51, 'PT_PT', 'Sistemas Embutidos e de Tempo Real' , 'Esta UC tem por objectivo dotar os alunos com capacidade de desenvolver sistemas embebidos com requisitos de tempo-real, recorrendo, se necessário, a sistemas operativos de tempo-real.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (52, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (52, 'PT_PT', 'Tecnologias de Bases de Dados' , 'Esta UC tem por objectivo habilitar os alunos a desenvolver e administrar grandes bases de dados, nomeadamente a afinar o esquema físico, a optimizar interrogações SQL, a tirar partido das extensões à manipulação de objectos e à tecnologia XML e a recuperar e replicar dados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (53, 4, 2, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (53, 'PT_PT', 'Tecnologias de Distribuição e Integração' , 'Os objectivos principais da unidade curricular incluem:\n- a aquisição de conhecimento e prática dos conceitos essenciais do projecto e arquitectura de aplicaçãoes distribuídas empresariais;\n- aplicação dos principais padrões e tecnologias, consoante os requisitos e funcionalidades dos cenários finais;\n- conhecimaento detalhado de várias tecnologias, com vista à sua implementação fluente, e à sua interoperabilidade e integração.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (54, 5, 1, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (54, 'PT_PT', 'Preparação da Dissertação' , 'A unidade curricular \"Preparação de Dissertação\" acompanha o processo de organização e trabalho preliminar da dissertação do Mestrado Integrado em Engenharia Informática e Computação. Os estudantes completam PDIS antes do semestre em que a dissertação é realizada a tempo inteiro. O esforço da unidade é o equivalente a 6 ECTS (1/5 de semestre a tempo inteiro) e é ocupado com a escolha e familiarização com o tema da dissertação, com o estudo do estado da arte no domínio da dissertação e com a realização de trabalho preliminar que permita no semestre da dissertação um trabalho intenso, bem focado e com boa apresentação de resultados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (55, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (55, 'PT_PT', 'Análise de Projectos de Investimento' , 'O objectivo desta disciplina é dotar os alunos do conhecimento das metodologias-base para a análise da viabilidade económica e financeira de projectos de investimento.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (56, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (56, 'PT_PT', 'Armazéns de Dados' , 'O objectvo da UC Armazém de Dadtos é dotar os alunos da capacidade de projectar, implementar e explorar armazéns de dados (data warehouses).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (57, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (57, 'PT_PT', 'Computação Móvel' , 'Os objectivos principais desta unidade curricular visam a aquisição de conhecimento e prática dos conceitos essenciais do projecto, arquitectura e implementação de aplicações, principalmente empresariais, em dispositivos móveis, utilizando APIs de grande divulgação corrente.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (58, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (58, 'PT_PT', 'Descrição, Armazenamento e Pesquisa de Informação' , 'A unidade curricular "Descrição, Armazenamento e Pesquisa de Informação" tem como objectivo:\n-Sensibilizar os estudantes para os problemas do armazenamento e organização de grandes coleções de dados;\n-Familiarizar os estudantes com os conceitos principais na pesquisa de documentos textuais e da sua aplicação em ferramentas;\n-Explorar os métodos e ferramentas para a descrição de recursos na web e para o uso da descrição em aplicações que fazem uso do significado dos dados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (59, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (59, 'PT_PT', 'Extracção de Conhecimento e Aprendizagem Computacional' , 'Os objectivos desta UC são:\n-Motivar para a utilização de técnicas de extração de conhecimento (EC) de dados, ou data mining, no apoio à decisão.\n-Desenvolver a capacidade de utilizar corretamente essas técnicas para análise automática de grandes quantidades de dados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (60, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (60, 'PT_PT', 'Interacção Pessoa-Computador' , 'O principal objectivo da disciplina é o de preparar os estudantes para analisarem e projectarem as componentes interactivas dos sistemas, em particular da interface com os utilizadores, de uma forma adequada às suas necessidades, considerando o curto, médio e longo prazo.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (61, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (61, 'PT_PT', 'Metodologias Ágeis de Desenvolvimento de Software' , 'Os principais objectivos da unidade curricular é dotar os estudantes de conhecimentos e experiência concreta na definição, utilização e refinamento de processos ágeis para a especificidade de um projecto.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (62, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (62, 'PT_PT', 'Realidade Virtual e Aumentada' , 'Realidade Aumentada é uma tecnologia através da qual se pretende melhorar ou aumentar a visão que um utilizador tem do mundo real com imagens virtuais, usando técnicas de Visão por Computador e de Computação Gráfica/Realidade Virtual. O principal objectivo desta disciplina é transmitir aos alunos um conjunto de conhecimentos básicos destas técnicas, que lhes permitam prosseguir estudos mais avançados na área emergente da Realidade Aumentada, e a capacidade de realizar pequenos trabalhos ilustrativos das metodologias estudadas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (63, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (63, 'PT_PT', 'Robótica' , 'Os principais objectivos da UC Robótica são:\n-Compreender os conceitos básicos de Robótica e o enquadramento da Inteligência Artificial na Robótica.\n-Estudar métodos de perceção e interpretação sensorial (com ênfase na visão por computador)\n-Estudar os fundamentos da robótica cooperativa e da construção de equipas de robôs\n-Analisar as principais competições robóticas nacionais e internacionais, os simuladores robóticos mais realistas e as plataformas robóticas mais avançadas disponíveis no mercado.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (64, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (64, 'PT_PT', 'Segurança em Sistemas Informáticos' , 'Esta Unidade Curricular tem por âmbito o estudo da problemática de segurança de sistemas Informáticos, de forma a fornecer aos estudantes um painel básico dos conceitos e das ameaças e defesas de uma utilização abusiva e destrutiva de tais sistemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (65, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (65, 'PT_PT', 'Seminário de Engenharia de Software e Sistemas de Informação' , 'O objectivo fundamental desta unidade curricular é aprender, discutir e desenhar soluções para as várias problemáticas que surgem pela influência da engenharia de software (e do uso do software em geral) pelas pessoas e pela sociedade como um todo.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (66, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (66, 'PT_PT', 'Seminário de Sistemas Inteligentes, Interacção e Multimédia' , 'Este Seminário tem como objectivos principais:\n-Tomar conhecimento e discutir sobre trabalhos de investigação em curso nas áreas dos Sistemas Inteligentes.\n-Tomar contacto com diferentes investigadores em Sistemas Inteligentes e, através deles, com vários grupos de investigação activos nos tópicos apresentados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (67, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (67, 'PT_PT', 'Serviços de Rede e de Sistema' , '(Não disponível)');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (68, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (68, 'PT_PT', 'Teste, Verificação e Validação de Software' , 'O objectivo desta unidade curricular é o de dotar os alunos com conhecimentos consolidados na área das redes de computadores sob uma prespectiva de projectores e gestores de uma rede, nomedadamente, constituição de várias sub-redes para satisfazer requisitos específicos de organizações e utilizadores, atribuição de gamas de enderecos IP, gestão de serviços de encaminhamento de pacotes dentro de um domínio e entre domínios, identificação de servicos de redes necessários, sua instalação e configuração.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (69, 5, 1, 6, true, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (69, 'PT_PT', 'Visão por Computador' , 'A visão por computador é uma área da ciência da computação focada na extracção de "informação útil" de imagens e vídeos. O objectivo da visão por computador é "descobrir a partir de imagens o que está presente no mundo, onde estão localizadas as coisas, que acções estão a ser executadas" (Marr,1982).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (70, 5, 2, 6, false, 1);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (70, 'PT_PT', 'Dissertação' , 'Trabalho individual de investigação e desenvolvimento, conducente à elaboração de uma dissertação de natureza científica sobre um tema da área de conhecimento do curso, ou visando a integração e aplicação à resolução de problemas complexos de engenharia de conhecimentos, competências e atitudes adquiridos ao longo do curso.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (71, 1, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (71, 'PT_PT', 'Análise Matemática' , 'Dotar os alunos dos conhecimentos básicos de Cálculo Diferencial e Integral para funções reais de uma variável real. Saber resolver equações diferenciais e aplicar os conhecimentos obtidos na modelação e resolução de problemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (72, 1, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (72, 'PT_PT', 'Estruturas Discretas' , 'Um curso de matemática discreta tem vários objectivos. Os estudantes deverão aprender um conjunto de factos matemáticos e como aplicá-los; mas, mais importante, deverão aprender a pensar matematicamente. Para tal, é realçado o raciocínio matemático e as diferentes formas de abordar e resolver problemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (73, 1, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (73, 'PT_PT', 'Introdução à Programação e Resolução de Problemas' , 'Aprender os princípios básicos de programação e gestão de dados num contexto de resolução de problemas. São exploradas as vertentes algorítmica e de modelação e manipulação de dados, sendo ainda introduzido contexto sobre sistemas informáticos em geral.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (74, 1, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (74, 'PT_PT', 'Tecnologia dos Computadores' , 'Tem como objectivos dar uma perspectiva geral das diversas áreas da informática. A componente prática concentra-se na aplicação de conceitos de electrónica digital utilizados no desenho de computadores.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (75, 1, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (75, 'PT_PT', 'Álgebra Linear e Geometria Analítica' , 'Dotar os alunos dos conhecimentos básicos de Álgebra Linear e Geometria Analítica. Nomeadamente, solucionar sistemas de equações lineares; efectuar operações algébricas com matrizes, compreender e relacionar conceitos e resultados fundamentais de espaços vectoriais, determinar projecções ortogonais e compreender a sua importância no contexto da optimização matemática, compreender e manusear os conceitos de valor próprio e a decomposição espectral de uma matriz');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (76, 1, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (76, 'PT_PT', 'Análise Matemática II' , 'O objectivo principal é dotar os alunos dos conhecimentos básicos de Cálculo Diferencial e Integral para funções de várias variáveis reais e de Análise Complexa. Saber resolver equações diferenciais lineares de coeficientes constantes (recorrendo às transformadas de Laplace) e equações de diferenças (recorrendo às transformadas de Z) e aplicar esses conhecimentos na modelação e resolução de problemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (77, 1, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (77, 'PT_PT', 'Arquitectura de Computadores' , 'Proporcionar ao aluno uma noção global do funcionamento de um sistema computacional, na perspectiva do programador e do projectista de sistemas digitais, com especial enfoque em: (i) conhecimentos sobre arquitecturas de computadores actuais e compromissos que devem ser tidos em consideração no desenvolvimento de um sistema computacional; (ii) programação em linguagem Assembly; (iii) compreensão da forma como o software interage com o hardware e consequente consolidação das competências de programação em linguagem C.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (78, 1, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (78, 'PT_PT', 'Estatística' , 'Introduzir conhecimentos básicos em modelação de comportamentos padrão de fenómenos aleatórios em contextos de Engenharia ou Ciência, contribuindo para uma formação capaz de descrever, analisar e interpretar situações reais através de modelos matemáticos não deterministas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (79, 1, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (79, 'PT_PT', 'Princípios de Programação Procedimental' , 'Permitir ao aluno adquirir um conjunto de conhecimentos sólidos sobre programação procedimental utilizando a linguagem C, com particular destaque para apontadores e estruturas de dados dinâmicas simples.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (80, 1, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (80, 'PT_PT', 'Tópicos de Física Moderna' , 'Transmitir aos alunos conceitos básicos e ferramentas de trabalho que permitem explicar, compreender e prever fenómenos em Física Clássica e em Física Quântica e a sua relação com Computação Quântica');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (81, 2, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (81, 'PT_PT', 'Introdução às Redes de Comunicação' , 'A disciplina de Redes de Comunicações pretende dotar os estudantes de um largo espectro de conhecimento nas áreas das comunicações e de capacidades para projectarem soluções técnicas de elevado potencial.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (82, 2, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (82, 'PT_PT', 'Programação Orientada aos Objectos' , 'O objectivo desta disciplina é dar ao aluno um conjunto de conhecimentos sólidos em Programação Orientada aos Objectos. Estes conhecimentos não se cingirão à sintaxe de uma linguagem de programação particular, mas incluirão aspectos relativos à modelação orientada aos objectos, assim como à sua representação gráfica.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (83, 2, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (83, 'PT_PT', 'Sistemas Operativos' , 'O objectivo desta disciplina é dar a conhecer aos estudantes o funcionamento dos SO modernos e ao mesmo tempo dotar os estudantes das devidas capacidades para desenvolverem aplicações de sistema que façam uso directo do interface de programação do sistema operativo e que explorem as capacidades de concorrência e multi-programa.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (84, 2, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (84, 'PT_PT', 'Teoria da Computação' , 'Estudar os diversos tipos de autómatos (finitos, de pilha, Máquinas de Turing) e de gramáticas formais (regulares, independentes e dependentes do contexto, recursivas) e as relações entre eles, bem como sua relação com linguagens de programação e compiladores.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (85, 2, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (85, 'PT_PT', 'Teoria da Informação' , 'Fornecer de uma forma sistemática as noções fundamentais da teoria da informação e a sua aplicação em contextos de aprendizagem computacional, análise de dados e, sobretudo, aos princípios da codificação nos contextos da codificação de fontes e de canal e na criptografia.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (86, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (86, 'PT_PT', 'Algoritmos e Estruturas de Dados' , 'Após a conclusão da unidade curricular deve demonstrar conhecimento e capacidade de compreensão nos seguintes aspectos: análise de complexidade; técnicas gerais de projecto de algoritmos; estruturas de dados e algoritmos de ordenamento (características, memória ocupada, velocidade de execução).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (87, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (87, 'PT_PT', 'Análise e Transformação de Dados' , 'A disciplina pretende desenvolver as seguintes competências:\n - Compreensão e interpretação de fenómenos e processos dinâmicos do mundo real e virtual\n - Capacidade de análise de dados suportada por ferramentas computacionais\n - Aprofundamento do raciocínio matemático para extracção de informação\n - Capacidade de resolução de problemas concretos no âmbito da análise e transformação de dados uni e multidimensionais no domínio do tempo, do espaço e da frequência');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (88, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (88, 'PT_PT', 'Multimédia' , 'Após a conclusão da unidade curricular, o estudante deve demonstrar ter adquirido, ter compreendido e saber aplicar os conhecimentos e competências em:\n i) projecto multimédia\n ii) fundamentos de multimédia\n Nomeadamente:\n - planeamento, design, desenvolvimento e gestão de projectos multimédia em equipa;\n - tópicos de usabilidade e sua aplicação em projectos multimédia;\n - imagem, áudio e vídeo digital, com enfoque nos aspectos fisiológicos e perceptuais envolvidos e na representação e compressão desses tipo de dados multimédia.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (89, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (89, 'PT_PT', 'Protocolos de Comunicação' , 'Os objectivos centrais da disciplina são o estudo e compreensão dos aspectos protocolares dos modernos sistemas de comunicação, sendo dada especial ênfase ao ambiente protocolar da Internet, tendo em vista um conhecimento das principais questões que caracterizam e condicionam os serviços e aplicações.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (90, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (90, 'PT_PT', 'Simulação e Computação Científica' , 'Esta é uma disciplina transversal, de vastíssimo campo de aplicação. Um dos objectivos fundamentais é a aquisição de um conjunto de conhecimentos e competências que permitam, perante uma situação específica, desenvolver um modelo de simulação, implementá-lo, validá-lo e utilizá-lo de forma correcta, tomando em consideração as vantagens e limitações do uso da simulação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (91, 2, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (91, 'PT_PT', 'Bases de Dados' , 'Esta UC debrucça-se sobre of fundamentos das bases de dados relacionais, incluindo modelo relacional, normalização, operações relacionais, linguagem SQL, transacções e controlo de concorrência, indexação, etc, Para além disso, também pretende operacionalizar estes conceitos através da análise, concepção e construção prática de bases de dados relacionais em ambiente cliente-servidor.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (92, 3, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (92, 'PT_PT', 'Comunicação Técnica' , 'No final do semestre, os estudantes serão capazes de:\n(i) utilizar diferentes formas de comunicação em contextos académicos e profissionais (ex: redigir um relatório);\n(ii) argumentar, utilizando códigos distintos, em questões de natureza social associadas ao exercício da profissão (ex: apresentações formais);\n(iii) adequar as suas formas de comunicação aos públicos-alvo (ex: textos técnicos e de divulgação).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (93, 3, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (93, 'PT_PT', 'Engenharia de Software' , 'O aluno deverá ser capaz de perceber porque razão a complexidade do software exige uma abordagem de engenharia e as diversas formas de organizar as pessoas e actividades para o desenvolvimento de um produto de qualidade, nomeadamente abordagens lineares, iterativas e incrementais. Deverá ainda ser capaz de perceber as diferenças entre elas e escolher a(s) mais indicada(s) em função do contexto concreto do projecto. Deverá também ser capaz de usar técnicas e artefactos genéricos de gestão de projecto (diagramas Gantt e PERT/CPM, análise de riscos,etc.). Finalmente, deverá ser capaz de descrever o artefacto de software a desenvolver usado a linguagem UML.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (94, 3, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (94, 'PT_PT', 'Processos de Gestão e de Inovação' , 'Nesta disciplina, os estudantes serão sensibilizados para a importância de mdeterminados conceitos, princípios e práticas da gestão da inovação, ao mesmo tempo que se irão familiarizar com algumas metodologias e ferramentas que permitam a sua operacionalização e implementação no terreno.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (95, 3, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (95, 'PT_PT', 'Sistemas Distribuídos' , 'Os alunos deverão adquirir competências no desenvolvimento de aplicações distribuídas usando várias tecnologias e deverão ser capazes de perceber e interligar conhecimentos sobre técnicas fundamentais para o desenho de aplicações distribuídas em ambiente empresarial e para a Internet.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (96, 3, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (96, 'PT_PT', 'Compiladores' , 'O estudante deverá passar a compreender: os objectivos e a arquitectura de um compilador; os princípios das análises lexical, sintática e semântica e saber implementar essas fases de um compilador, quer de raíz, quer usando ferramentas adequadas; alguns princípios de geração do código final.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (97, 3, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (97, 'PT_PT', 'Computação Gráfica' , 'Nesta unidade curricular pretende-se que o aluno adquira conhecimentos sobre temas centrais à Computação Gráfica, tanto a nível de fundamentos como a nível de utilização de ferramentas gráficas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (98, 3, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (98, 'PT_PT', 'Introdução à Inteligência Artificial' , 'Pretende-se que o aluno adquira conhecimentos de base sólidos sobre a área da inteligência artificial ao nível dos fundamentos, técnicas e aplicação prática. Para o efeito é adoptado o conceito integrador de Agente. Estuda-se o desenvolvimento de agentes de complexidade e capacidade crescente seguindo três metáforas: Simbólica, Conexionista e Biológica.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (99, 3, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (99, 'PT_PT', 'Laboratórios de Programação Avançada' , 'Resolução de problemas avançados típicos de programação. Aula maioritariamente prática.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (100, 3, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (100, 'PT_PT', 'Sistemas de Informação' , 'Esta uc proporciona aos alunos as competências e ferramentas que permitem discutir o projecto e evolução de sistemas de informação (SI) com responsáveis de negócio. Ao completar o curso, os alunos compreenderão as diferentes e frequentemente contraditórias perspectivas dos responsáveis de negócio e dos informáticos, tornando-se assim interlocutores privilegiados para ajudar ambas as partes a planear o SI da organização.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (101, 4, 1, 4, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (101, 'PT_PT', 'Empreendedorismo e Gestão de Empresas' , 'São objectivos desta disciplina estimular a criação de competências de empreendedorismo e espírito inovador e familiarizar os futuros profissionais com a realidade do tecido empresarial e os fundamentos teóricos da criação e gestão de pequenas empresas e negócios numa óptica de criação de valor.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (102, 4, 1, 4, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (102, 'PT_PT', 'Interacção Humano-Computador' , 'Através desta unidade curricular os estudantes ficarão a conhecer os conceitos fundamentais para compreender a interação entre humanos e sistemas computacionais. O estudante deverá:\n - compreender o papel dos factores humanos na interação e interface;\n - compreender e aplicar técnicas de prototipagem para investigação e ensaio de proposta de design;\n - desenvolver a capacidade para conceber propostas de design de interação e interface para contextos de uso específicos tendo em vista a optimização da experiência de interação, com base em abordagens contextuais e participativas;\n - desenvolver a capacidade de avaliação da usabilidade com domínio de técnicas variadas e sua adequação ao contexto de projecto;\n - desenvolver a capacidade de construção de interfaces inovadoras e coerentes com o propósito;\n - desenvolver a capacidade de combinar as técnicas de uma forma sistemática numa abordagem empírica ao design de interação;\n - desenvolver a capacidade de aprendizagem autónoma no domínio de HCI.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (103, 4, 1, 4, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (103, 'PT_PT', 'Metodologias Experimentais em Informática' , 'Fornecer os métodos experimentais necessários para efetuar avaliação empírica de desempenho de sistemas computacionais que surgem nas várias áreas da informática, de redes de comunicação a algoritmos e inteligência artificial. Aquisição de competências ao nível da análise e síntese, organização e planeamento.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (104, 4, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (104, 'PT_PT', 'Modelação e Análise de Sistemas' , 'Esta disciplina foca-se em modelos para a análise de sistemas, com ênfase em problemas de optimização e sistemas de eventos discretos, com aplicações às quatro opções temáticas do curso. Pretende-se que os estudantes venham a ser capazes de:\n 1) formular problemas de optimização, categorizando-os em função das suas características e formalizando-os de modo a poderem ser resolvidos pelos métodos mais adequados\n 2) modelar e analisar sistemas simples de tráfego, de produção, computacionais (hardware e software) e de comunicação enquanto sistemas de eventos discretos\n Os estudantes deverão ainda desenvolver competências em análise e síntese, resolução de problemas, raciocínio crítico, aprendizagem autónoma, e a capacidade de aplicar o conhecimento teórico adquirido a situações práticas concretas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (105, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (105, 'PT_PT', 'Fundamentos de Sistemas de Comunicação' , 'Os objectivos principais desta UC são o estudo dos fundamentos dos sistemas de transmissão e de comutação. Os tópicos incluem: modulação e transmissão de sinais, sistemas de transmissão e de comutação, aspectos de sinalização em sistemas digitais. Esta UC trata dos aspectos dos sistemas relacionados com redes fixas ou móveis e satélites, e com a transmissão de sinais de voz e vídeo. A UC também cobre os fundamentos dos protocolos de comunicação relacionados com controlo de erros, controlo de fluxo e especificação de protocolos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (106, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (106, 'PT_PT', 'Sistemas de Comunicação Móvel' , 'Objectivos centrais da UC são, por um lado, a abordagem e estudo, teórico e prático, dos principais aspectos relacionados com a mobilidade de sistemas e redes e, por outro, a exploração de protocolos, tecnologias e aplicações de redes de sensores.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (107, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (107, 'PT_PT', 'Engenharia de Requisitos' , 'Nesta unidade curricular os estudantes irão estudar diversos métodos para compreender o problema com que se deparam, os factores que condicionam a gama de soluções possíveis, e as abordagens para decidirem entre essas alternativas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (108, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (108, 'PT_PT', 'Integração de Sistemas' , 'Nesta disciplina abordamos múltiplas formas estruturadas de integrar aplicações, não só ao nível de uma organização, mas para além dela. É dada uma forte ênfase à integração utilizando middleware, em particular, utilizando abordagens como SOA (Service Oriented Architecture) e ESB (Enterprise Service Bus).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (109, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (109, 'PT_PT', 'Aprendizagem Computacional' , 'Estudar  as principais técnicas de aprendizagem computacional no contexto da multiplicidade de tipos de dados disponíveis nas aplicações relevantes nomeadamente desenvolvimento e interpretação de árvores de decisão , configuração e treino de redes neuronais artificiais, de lógica difusa, sistemas difusos e neuro-difusos, e configuração e treino de máquinas de vectores de suporte. Desenvolver as competências para conceber sistemas de classificação de grandes conjuntos de dados, de diagnóstico em contextos industriais e médicos, de controlo inteligente, de análise sistémica dos problemas complexos e de avaliação crítico dos resultados.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (110, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (110, 'PT_PT', 'Inteligência Artificial' , 'Fornecer aos estudantes conceitos, princípios e teorias avançadas necessárias para o desenvolvimento de aplicações reais envolvendo agentes ou sistemas com capacidades de raciocínio, comportamento e interação com os seus ambientes de forma inteligente.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (111, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (111, 'PT_PT', 'Arquitectura de Empresas' , 'Uma arquitectura de empresa tenta descrever e controlar a estrutura de uma organização, processos, aplicações e sistemas de forma integrada. A especificação não ambígua dos componentes e das suas relações numa arquitectura requer uma linguagem de modelação coerente. Tal linguagem deve permitir a modelação integrada de vários domínios da arquitectura e deve ser analisada por pessoas de TI e por pessoas com um background empresarial. Nesta unidade curricular, apresentamos uma linguagem de modelação de empresas que capta a complexidade dos domínios de uma arquitectura, as suas relações e permite a construção de modelos integrados. A linguagem fornece aos arquitectos instrumentos concretos para que possam melhorar as suas práticas arquitectónicas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (112, 4, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (112, 'PT_PT', 'Gestão de Sistemas de Informação' , 'Esta unidade curricular explica aos alunos os principais conceitos, princípios e metodologias necessárias para gerir SI em organizações. A unidade também fornece competências necessárias em síntese, comunicação escrita, resolução de problemas, raciocínio crítico e aplicação prática de conhecimento em áreas como governação de IT, governação de Big Data, e metodologias de gestão, tais como o ITIL e o COBIT.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (113, 4, 2, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (113, 'PT_PT', 'Segurança em Tecnologias da Informação' , 'Esta UC pretende dotar os alunos das capacidades técnicas para administração da segurança em TI. Aspectos de sistemas de comunicação, redes informáticas, sistemas de informação organizacionais e sistemas distribuídos. Compreender o conjunto de métodos e ferramentas para a realização das tarefas associadas a um especialista em Segurança em TI.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (114, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (114, 'PT_PT', 'Gestão de Infra-estruturas e Serviços na Internet' , 'Os objectivos centrais da disciplina são a abordagem teórica e prática à temática de gestão de infra-estruturas informáticas, incluindo o planeamento e gestão dos serviços de rede que integram e/ou dão suporte a essas infra-estruturas, o suporte ás operações e manutenção dessas infra-estruturas (planeamento, monitorização, gestão de falhas, gestão de níveis de serviço, AAA, etc.), os protocolos de suporte à gestão de redes e serviços informáticos, e a administração de serviços e infra-estruturas informáticas em ambientes virtualizados e/ou instalados em ambientes cloud.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (115, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (115, 'PT_PT', 'Modelação e Desempenho de Redes' , 'O objectivo principal da disciplina é o estudo de metodologias para a modelação e avaliação do desempenho de redes de comunicação. A disciplina aborda os fundamentos da teoria das probabilidades e dos processos estocásticos na perspectiva da modelação e análise de desempenho das redes de comunicação. De forma complementar, a disciplina aborda metodologias de simulação para avaliação de desempenho de sistemas de comunicação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (116, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (116, 'PT_PT', 'Arquitectura de Software' , 'Após a conclusão desta unidade curricular, os alunos deverão:\n - ser capazes de descrever uma arquitectura com precisão;\n - reconhecer os principais estilos arquitecturais existentes em sistemas de software;\n - propor alternativas de arquitectura para um problema, e escolher entre eles;\n - ter as competências necessárias para a construção de um sistema de software de dimensão média que satisfaça uma especificação de arquitectura;\n - usar as ferramentas de desenvolvimento correctas para acelerar essas tarefas;\n - compreender a definição formal de uma arquitectura e ser capaz de raciocinar sobre as suas propriedades;\n - usar o conhecimento de domínio para especializar uma arquitectura para uma determinada família de aplicações.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (117, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (117, 'PT_PT', 'Qualidade e Confiabilidade de Software' , 'Esta unidade curricular aborda com profundidade as áreas da engenharia de software relacionadas com a verificação, validação e certificação. O objectivo é dotar os alunos com conhecimento abrangente sobre qualidade de software e as actividades técnicas levadas a cabo para assegurar a qualidade e a confiabilidade do software, incluindo análise estática e dinâmica, modelos de desenvolvimento e standards, e certificação de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (118, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (118, 'PT_PT', 'Computação Evolucionária' , 'Apresentar, discutir e desenvolver soluções de engenharia de inspiração natural (i.e., biológica, social, física) para problemas de elevada complexidade que, ou não têm solução analítica, ou são computacionalmente intratáveis. Aprender a avaliar de modo rigoroso, i.e., por recurso à estatística, soluções alternativas para os problemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (119, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (119, 'PT_PT', 'Reconhecimento de Padrões' , 'O Reconhecimento de Padrões (RP) estuda a concepção, desenvolvimento e implementação de sistemas capazes de reconhecer padrões nos dados. Esta UC ministra os algoritmos que permitem explorar aplicações de RP, formalizando-as, com modelos analíticos de uso alargado nos mais diversos domínios. Conceitos básicos, modelos e ferramentas para a compreensão e desenho de um SRP.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (120, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (120, 'PT_PT', 'Engenharia de Serviços' , 'A engenharia de serviços fornece um contributo importante para a definição, desenho, modelação, execução e análise de serviços. A unidade curricular apresenta linguagens de modelação, métodos, metodologias, aplicações e casos de estudo com resultados accionáveis, ilustrando as técnicas que podem ser utilizadas em larga escala com recurso a exemplos do mundo real para projectar serviços.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (121, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (121, 'PT_PT', 'Gestão de Processos de Negócio' , 'A unidade curricular de Gestão de Processos de Negócio introduz conceitos e abordagens de gestão e melhoria de processos de negócio. Foca-se na compreensão, desenho e optimização de processos.\n Os estudantes aprenderão a identificar, desenhar, documentar, modelar, avaliar e melhorar processos de negócio,  apercebendo-se dos desafios inerentes. Perceberão a forma como as tecnologias de informação podem ser usadas para automatizar, gerir, transformar e melhorar processos de negócio, ganhando competências na utilização de algumas ferramentas. Compreenderão desafios, vantagens e riscos envolvidos em processos inter-organizacionais e em outsourcing de processos de negócio.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (122, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (122, 'PT_PT', 'Sistemas Complexos' , 'Esta cadeira tem por objectivo principal o estudo de sistemas complexos, sejam eles físicos (e.g., o clima), biológicos (e.g. redes de regulação genética), cognitivos (e.g. a mente) ou sociais (e.g., os mercados financeiros). Focaremos os conceitos, os modelos e as ferramentas necessários à compreensão destes sistemas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (123, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (123, 'PT_PT', 'Sistemas Ubíquos' , 'Nesta disciplina são adquiridos os conhecimentos necessários à concepção e desenvolvimento de sistemas ubíquos e à sua integração com os sistemas de informação existentes. É dada especial atenção às metodologias de posicionamento, representação de lugares e percepção de contexto, privacidade e segurança e avaliação de sistemas ubíquos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (124, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (124, 'PT_PT', 'Sistemas de Gestão de Dados' , 'Aborda-se conceitos de sistemas de gestão de bases de dados (SGBD) convencionais, sistemas de gestão para bigdata, escalabilidade e performance. Aborda-se também sistemas de gestão de dados em outros ambientes. Os conceitos básicos incluem hardware, organização física, índices, processamento e optimização de pesquisas, transacções, backups e recuperação, bem como aspectos relacionados com o desenvolvimento de aplicações. Para além destes, a unidade curricular focará colocação e gestão de dados em ambientes paralelos, distribuídos e móveis, questões de replicação, consistência, transacções e disponibilidade nesses ambientes. São abordados temas actuais (e que podem evoluir ao longo dos anos), tais como Map-Reduce e Hive, bases de dados verticais, sistemas NoSQL.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (125, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (125, 'PT_PT', 'Visão por Computador' , 'Perceber os aspectos relativos à formação geométrica e radiométrica das imagens. Aprender métodos para estimar elementos na imagem e relacioná-los com as estruturas 3D. Aprender métodos para a reconstrução 3D e para a estimação de movimento 3D.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (126, 4, 2, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (126, 'PT_PT', 'Business Intelligence' , 'Esta disciplina tem por objectivos estudar as principais metodologias de desenvolvimento de soluções de Business Intelligence e mostrar como as técnicas de Data Warehouses(DW), de OLAP e de Data Mining(DM) podem ser conjugadas na construção de soluções informáticas de apoio à decisão.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (127, 5, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (127, 'PT_PT', 'Gestão de Projectos' , 'A natureza da actividade de um engenheiro informático é de tal forma única, que requer o conhecimento de técnicas de gestão de projectos, nomeadamente a nível da gestão de recursos, quer humanos quer computacionais, análise de riscos, planeamento, acompanhamento, etc. Nesta disciplina pretende-se abordar as principais técnicas de gestão de projecto enquanto actividade de engenharia, i.e. com custos e prazos finitos, incluindo aspectos de relacionamento pessoal e organizacional, como resultado da necessidade de trabalhar no seio de equipas de razoável dimensão (8 a 12 pessoas), em projectos reais com clientes reais.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (128, 5, 1, 6, false, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (128, 'PT_PT', 'Dissertação/Estágio' , 'Os objectivos principais do estágio são os seguintes:\n - Técnicas de projecto e desenvolvimento de software e sistemas de informação e comunicação\n - Realização de projectos de desenvolvimento tecnológico\n - Contacto com a elaboração de projectos em ambiente empresarial\n - Iniciação a actividades de investigação de base e aplicada\n - Integração no mercado laboral\n Realização de um documento com a proposta de estágio / dissertação incluindo os seguintes aspectos:\n - Análise do estado da arte\n - Escolha justificada das ferramentas e metodologias a utilizar\n - Análise de requisitos relativamente ao tema a desenvolver\n - Especificação de alto nível do sistema a desenvolver / trabalho a realizar incluindo objectivos concretos e calendarização para o segundo semestre.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (129, 5, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (129, 'PT_PT', 'Informática Médica' , 'Fornecer de uma forma sistemática as tecnologias e os fundamentos para a integração e desenvolvimento de sistemas de informação e de apoio à decisão em aplicações clínicas. Em particular, é objectivo exporem-se os protocolos e abordagens mais significativas em sistemas de informação clínicos, as arquitecturas e tecnologias utilizadas para a construção de sistemas tele-médicos (nomeadamente de sistemas pHealth), e ainda algoritmos de análise de informação clínica para apoio ao diagnóstico.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (130, 5, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (130, 'PT_PT', 'Robótica' , 'Pretende-se fornecer conhecimento (in-depth) em Robótica para aquisição de competências a todos:\n 1. aqueles que pretendam operar e/ou desenvolver aplicações que usem robots ou sistemas robóticos;\n 2. aqueles que pretendam desenvolver/projectar sistemas robóticos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (131, 5, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (131, 'PT_PT', 'Serviços Emergentes da Internet' , 'Os objectivos da disciplina são o conhecimento de fundamentos de aplicações telemáticas, aplicações para a Internet e de serviços emergentes na Internet. Serão também abordados os paradigmas, tecnologias e protocolos para o suporte de aplicações multimédia na Internet. Os alunos deverão desenvolver competências de análise crítica e exposição das soluções actuais e dos desafios futuros nas áreas das aplicações telemáticas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (132, 5, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (132, 'PT_PT', 'Serviços e Infra-estruturas de Alto Desempenho' , 'Os objectivos centrais da disciplina são a abordagem teórica e prática à temática de gestão de serviços e infra-estruturas informáticas de alto desempenho, incluindo o planeamento e administração dessas infra-estruturas (especificação e desenho de centros de dados, operações e manutenção, gestão de recursos, aspectos de eficiência energética), o planeamento e administração desses serviços e das comunicações que lhe dão suporte (resiliência, qualidade de serviço, gestão de acordos de nível de serviço, eficiência energética).');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (133, 5, 1, 6, true, 2);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (133, 'PT_PT', 'Web Semântica' , 'O objectivo principal da cadeira de Web Semântica é que o aluno adquira um conjunto de competências: - Capacidade de construção e gestão de ontologias\n - Capacidade de representar uma ontologia numa linguagem da Web Semântica (RDF, RDFa, RDFs e OWL)\n - Capacidade para utilizar triple stores, SPARQL, Pesquisa Semântica e os mecanismos de raciocínio\n - Capacidade de aplicação e uso de ontologias representadas através de linguagens da Web Semântica, em aplicações para a Internet ou Intranets\n - Capacidade de especificação, design e desenvolvimento de sistemas/aplicações para a Web Semântica\n - Capacidade de reutilização de ontologias disponibilizadas na Internet');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (134, 1, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (134, 'PT_PT', 'Álgebra Linear' , 'Formação básica em Álgebra Linear. Domínio das seguintes matérias: espaços vectoriais, transformações lineares, espaços euclideanos, valores e vectores próprios.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (135, 1, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (135, 'PT_PT', 'Cálculo Diferencial e Integral I' , 'Formação básica em Análise Matemática. Domínio das seguintes matérias: sucessões, cálculo diferencial e integral de funções reais de uma variável real. Introdução às séries numéricas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (136, 1, 1, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (136, 'PT_PT', 'Fundamentos da Programação' , 'Fornecer conhecimentos sobre conceitos fundamentais relativos à actividade de programação, nomeadamente, algoritmo, abstracção procedimental e abstracção de dados, a programação como construção de abstracções, paradigmas de programação. Após a frequência da cadeira, os alunos deverão dominar os conceitos apresentados e serem capazes de desenvolver programas numa linguagem de programação de alto nível, o Python.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (137, 1, 1, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (137, 'PT_PT', 'Introdução à Arquitectura de Computadores' , 'Os alunos deverão dominar os conceitos básicos e o funcionamento geral de um computador, a estrutura material que permite a execução de programas. Deverão conseguir descrever o funcionamento dos elementos básicos, processador, sistema de memória e sistema de entradas e saídas, e sua interacção. Em particular, devem ser capazes de compreender a estrutura interna de um processador e o processo de execução das instruções.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (138, 1, 1, 3, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (138, 'PT_PT', 'Introdução à Engenharia Informática' , 'Esta UC tem como objectivo principal dar aos alunos uma visão da engenharia informática na sociedade, nas empresas, na inovação e na investigação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (139, 1, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (139, 'PT_PT', 'Cálculo Diferencial e Integral II' , 'Esta UC providencia os alunos com o domínio do cálculo diferencial e integral de funções de mais de uma variável real, incluindo os teoremas fundamentais do cálculo. Aplicações à Física.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (140, 1, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (140, 'PT_PT', 'Introdução aos Algoritmos e Estruturas de Dados' , 'Ganhar conhecimentos de programação numa linguagem imperativa. Adquirir conhecimentos sobre algoritmos básicos de ordenação e procura. Saber seleccionar, criar e utilizar estruturas de dados elementares. Saber projectar algoritmos iterativos e recursivos, para a resolução de problemas. Saber analisar a complexidade dos algoritmos utilizados para resolver um dado problema por forma a poder escolher aqueles que sejam mais eficientes.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (141, 1, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (141, 'PT_PT', 'Lógica para Programação' , 'Com a frequência da disciplina pretende-se que os alunos tenham conhecimento sobre os componentes de uma lógica, sejam capazes de desenvolver provas no sistema dedutivo, sejam capazes de utilizar o formalismo semântico, sejam capazes de relacionar o sistema dedutivo e o sistema semântico e provar propriedades sobre estas relações. Deverão obter noções sobre o raciocínio automático e ser capazes de desenvolver programas utilizando o paradigma da programação em lógica.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (143, 1, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (143, 'PT_PT', 'Matemática Discreta' , 'Desenvolver o raciocínio matemático rigoroso. Dominar os conceitos e instrumentos matemáticos necessários para a análise de procedimentos e algoritmos, quer quanto à sua correcção, quer quanto à sua eficiência.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (394, 2, 1, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (394, 'PT_PT', 'Análise Complexa e Equações Diferenciais' , 'Formação básica em: funções de uma variável complexa, equações diferenciais ordinárias, métodos de análise de Fourier com aplicação à resolução de equações diferenciais parciais.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (144, 2, 1, 4.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (144, 'PT_PT', 'Gestão' , 'Pretende-se que os alunos fiquem habilitados com a introdução às competências profissionais fundamentais para o funcionamento das organizações tais como: Enquadramento Microeconómico, Gestão Estratégica, Marketing, Gestão de Recursos Humanos, Contabilidade e Avaliação de Projectos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (145, 2, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (145, 'PT_PT', 'Mecânica e Ondas' , 'Esta disciplina apresenta os conceitos e princípios básicos da mecânica clássica, dos fenómenos ondulatórios, e da relatividade restrita, reforçando a compreensão desses conceitos através de aplicações ao mundo real. Os alunos deverão ter a capacidade de manipular esses conceitos e saber aplica-los à resolução de problemas. Os estudantes serão motivados por exemplos de aplicação dos princípios da física noutras áreas do conhecimento científico e tecnológico.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (146, 2, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (146, 'PT_PT', 'Programação com Objectos' , 'Conhecer: o paradigma de programação com objectos, as suas vantagens, inconvenientes e limitações;o paradigma dos padrões de desenho, tanto no desenvolvimento, como na refactorização de aplicações. Competências a adquirir: domínio do paradigma da programação com objectos, nomeadamente dos conceitos de encapsulamento, abstracção, herança e polimorfismo; capacidade de mutilizar uma linguagem de programação baseada em objectos; capacidade de utilização de padrões de desenho; e domínio da escrita de testes de avaliação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (147, 2, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (147, 'PT_PT', 'Sistemas Operativos' , 'Conhecer os conceitos fundamentais subjacentes aos sistemas operativos, com ênfase nos seus mecanismos e algoritmos, assim como a estrutura interna dos sistemas operativos mais relevantes. Programar, ao nível sistema, usando todas as funcionalidades dos sistemas operativos tendo em conta os modelos de programação sequencial e concorrente.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (148, 2, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (148, 'PT_PT', 'Análise e Síntese de Algoritmos' , 'Formação de nível intermédio em algoritmia e complexidade, familiarizando os alunos com técnicas de análise e síntese de algoritmos e estruturas de dados. Conhecimento dos fundamentos da análise e síntese de algoritmos. Análise da realização prática de algoritmos e estruturas de dados. Perspectiva abrangente das aplicações dos algoritmos em Engenharia Informática.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (149, 2, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (149, 'PT_PT', 'Electromagnetismo e Óptica' , 'Esta disciplina apresenta os conceitos e princípios básicos do electromagnetismo e da óptica física reforçando a compreensão desses conceitos através de aplicações ao mundo real. Os alunos deverão ter a capacidade de manipular esses conceitos e saber aplica-los à resolução de problemas. Os estudantes serão motivados por exemplos de aplicação dos princípios da física noutras áreas do conhecimento científico e tecnológico.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (150, 2, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (150, 'PT_PT', 'Interfaces Pessoa Máquina' , 'Aprender os princípios e regras fundamentais para a concepção e desenvolvimento de Interfaces Utilizador usáveis. Identificar os utilizadores e as tarefas que estes desejam realizar com o sistema interactivo a ser desenvolvido. Saber avaliar as interfaces em diferentes fases do seu desenvolvimento, aplicando as técnicas de avaliação que mais se adequam. Identificar os factores críticos no desenho de interfaces. Compreender e adoptar compromissos entre as várias restrições a que está sujeito o desenho da interface. Enquadrar o desenho de interfaces no projecto de engenharia informática.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (151, 2, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (151, 'PT_PT', 'Probabilidades e Estatística' , 'Iniciação ao estudo da teoria das probabilidades e inferência estatística, tendo em vista a compreensão e aplicação dos seus principais conceitos e métodos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (152, 2, 4.5, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (152, 'PT_PT', 'Teoria da Computação' , 'Entender e saber utilizar os conceitos de máquina de Turing, computação e recurso computacional. Compreender e aprofundar os conceitos de "tarefa" algorítmica e de "tarefa" não algorítmica. Conhecer os limites para "tarefas" algorítmicas. Entender a computação como conceito físico-matemático e não como conceito puramente matemático. Experimentar conceitos e técnicas em ambientes computacionais interactivos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (153, 3, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (153, 'PT_PT', 'Bases de Dados' , 'A disciplina faz uma introdução à concepção e análise de bases de dados. O curso foca no modelo relacional, cobrindo o desenho lógico das bases de dados (desenho do esquema) e sua implementação, bem como os sistemas de gestão de transações. Alguns aspectos dos sistemas de bases de dados paralelas e distribuídas, gestão de informação não estruturada e semiestruturada serão também abordados. A disciplina tem como objectivo expor os alunos aos conceitos básicos necessários no desenho e concepção de um sistema de gestão de base de dados, assim como questões práticas da sua realização no âmbito de um projecto em equipa.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (154, 3, 1, 4.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (154, 'PT_PT', 'Computação Gráfica' , 'Esta unidade curricular pretende fornecer conhecimentos básicos sobre computação gráfica interactiva. São leccionados os fundamentos da representação e criação de imagens sintéticas a partir de cenas tri-dimensionais. Neste âmbito os alunos verão dominar as transformações geométricas elementarese compreender o funcionamento do pipeline de visualização 3D e dos seus andares principais. No final, os alunos estarão preparados para desenhar e desenvolver aplicações gráficas interactivas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (155, 3, 1, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (155, 'PT_PT', 'Inteligência Artificial' , 'Introduzir o tema de Inteligência Artificial ao aluno, de forma a que adquira os conhecimentos básicos desta área da informática e o motive a seguir a área da Inteligência Artificial. Servir de base a cadeiras mais avançadas de Inteligência Artificial.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (156, 3, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (156, 'PT_PT', 'Organização de Computadores' , 'Os alunos deverão adquirir uma visão abrangente e actual das arquitecturas de computadores ao nível dos seus diversos componentes: processadores, sistema de memória e de entradas e saídas. Os alunos deverão ainda ser capazes de compreender as condicionantes para a evolução dos sistemas computacionais e de avaliar estratégias para optimização de programas tendo em atenção a arquitectura computacional subjacente, baseadas em métricas de desempenho.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (157, 3, 1, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (157, 'PT_PT', 'Redes de Computadores' , 'Saber analisar de forma crítica as várias opções tomadas na concepção de redes de computadores, suas arquitecturas e protocolos. Conhecer os protocolos principais da Internet. Saber programar aplicações de rede usando a interface de sockets.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (158, 3, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (158, 'PT_PT', 'Análise e Modelação de Sistemas' , 'Dotar os alunos com as competências fundamentais para a análise de problemas de engenharia em que a solução se pode conceptualizar como um sistema, recorrendo a técnicas de engenharia de requisitos e desenvolvimento de modelos conceptuais (especialmente em UML, SysML e BPMN)');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (159, 3, 2, 6, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (159, 'PT_PT', 'Compiladores' , 'Aquisição de conceitos sobre a definição de linguagens de programação. Obtenção de conhecimentos sobre os métodos de análise (front-end) e síntese (back-end) mais usados pelos compiladores. Especificação lexical e sintáctica, bem como a análise semântica das linguagens de computação, com especial ênfase nas linguagens de programação. Desenvolver um compilador para uma linguagem de programação simples que gere código máquina executável.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (160, 3, 2, 3, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (160, 'PT_PT', 'Computação e Sociedade' , 'Transmitir aos futuros engenheiros informáticos a noção do enorme impacto social que os aspectos éticos têm no desenvolvimento e utilização de produtos e serviços baseados em tecnologias de informação. Ensinar os fundamentos necessários para que possam fazer as escolhas acertadas quando confrontados com situações concretas envolvendo o desenvolvimento, operação e utilização de sistemas de informação.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (161, 3, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (161, 'PT_PT', 'Engenharia de Software' , 'Transmitir um roteiro do desenvolvimento de software, desde o levantamento de requisitos até à manutenção de programas. Integrar os conhecimentos adquiridos noutras disciplinas no contexto mais alargado do processo de desenvolvimento. Motivar para o desenvolvimento de software como uma engenharia, que integra os aspectos tecnológicas da computação com os factores sociais e humanos da construção de produtos.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (162, 3, 2, 7.5, false, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (162, 'PT_PT', 'Sistemas Distribuídos' , 'Analisar as arquitecturas e as tecnologias que permitem desenvolver aplicações distribuídas que possam garantir requisitos não funcionais como a reconfigurabilidade, a segurança, a tolerância a faltas e a escalabilidade. Aquisição de conhecimentos que permitam desenvolver uma aplicação com uma arquitectura distribuída. Programar aplicações distribuídas. Interpretar requisitos de segurança formulando políticas e concretizando mecanismos seguros no código da aplicação. Análise de requisitos de fiabilidade e disponibilidade e introdução de mecanismos de tolerância a faltas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (163, 4, 1, 7.5, true, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (163, 'PT_PT', 'Arquitecturas de Software' , 'Formar nos métodos, técnicas e linguagens do desenho de software de qualidade utilizando as abordagens de arquitecturas de software e padrões de desenho. Estudar os métodos e as técnicas da passagem do espaço do problema para o espaço da solução de modo a garantir a rastreabilidade entre os requisitos do sistema e o seu desenho. Ler, avaliar e escrever arquitecturas de software.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (164, 4, 1, 7.5, true, 3);
INSERT INTO DegreeClassTranslations(degreeClass, language, name, description) VALUES  (164, 'PT_PT', 'Especificação de Software' , 'Aprender técnicas formais para especificação de requisitos e desenvolvimento de software. Entender a importância e aprender métodos para a verificação da correcção de programas. Utilização de ferramentas automáticas para verificação programas.');
INSERT INTO DegreeClasses(id, year, semester, ects , isOptional, degree) VALUES (165, 4, 1, 7.5, true, 3);
